import Cart from "../cart";
import { Swiper, SwiperSlide } from "swiper/react";
import { Link } from "react-router-dom";
import React, { ReactNode } from "react";
interface YourComponentProps {
  title?: string;
  data: Array<any>;
  updateCard?: any;
}
const Slider: React.FC<YourComponentProps> = ({ title, data, updateCard }) => {
  const showCarts = (): ReactNode[] => {
    return data.map((cart, index) => (
      <SwiperSlide key={index}>
        <Link to={`/cart/${cart.id}`}>
          <Cart cart={cart} updateCard={updateCard} />
        </Link>
      </SwiperSlide>
    ));
  };
  return (
    <section className="popular">
      <h2 className="popular__title title">{title}</h2>
      <div className="popular__slider slider">
        <Swiper className="slider__row" spaceBetween={35} slidesPerView={1.3}>
          {showCarts()}
        </Swiper>
      </div>
    </section>
  );
};
export default Slider;
