import React, { useEffect, useState } from "react";
import DrawerCart from "../drawerCart";
import { Link } from "react-router-dom";

interface PropsType {
  isLikeOpen?: boolean | undefined;
  setOpenLike?: (status: boolean) => void;
  isBasketOpen?: boolean | undefined;
  setOpenBasket?: (status: boolean) => void;
  updateCardLike?: any;
  updateCardAdd?: any;
  carts?: [];
}
interface header {
  likeMock: any;
  updateCardLike: any;
  addMock: any;
  updateCardAdd: any;
  counterLike: number;
  counterAdd: number;
}
interface Like {
  likeMock: any;
  setOpenLike: any;
  classeForButton: any;
  counter: number;
}

interface Add {
  setOpenAdd: any;
  classeForButton: any;
  counter: number;
}

const LikedCart: React.FC<PropsType> = ({
  isLikeOpen,
  setOpenLike,
  carts,
  updateCardLike,
}) => {
  const dataLenght = carts?.length;
  return (
    <section className={isLikeOpen ? "like like--active" : "like"}>
      <div className="like__wrapper">
        <h2 className="like__title">
          Уподобане:
          <span
            className="like__exit "
            onClick={() => setOpenLike && setOpenLike(!isLikeOpen)}
          ></span>
        </h2>

        {dataLenght ? (
          <div className="like__block">
            {carts.map((cart: any, index: number) => {
              return (
                <DrawerCart
                  updateCard={updateCardLike}
                  Cart={cart}
                  smallButton={false}
                  action="like"
                  key={index}
                />
              );
            })}
          </div>
        ) : (
          <div className="like__nothing">
            <h2 className="like__text">Ви ще нічого не вподобали</h2>
            <button
              className="like__button"
              onClick={() => setOpenLike && setOpenLike(!isLikeOpen)}
            >
              ВПОДОБАТИ
            </button>
          </div>
        )}
      </div>
    </section>
  );
};

const Basket: React.FC<PropsType> = ({
  isBasketOpen,
  updateCardAdd,
  setOpenBasket,
  carts,
}) => {
  const [number, setNumber] = useState("");
  const dataLenght = carts?.length;
  return (
    <section className={isBasketOpen ? "drawer drawer--active" : "drawer"}>
      <div className="drawer__wrapper">
        <h2 className="drawer__title">
          Ваше замовлення:{" "}
          <span
            className="drawer__exit "
            onClick={() => setOpenBasket && setOpenBasket(!isBasketOpen)}
          ></span>
        </h2>
        {dataLenght ? (
          <>
            <div className="drawer__carts-block">
              {carts.map((cart: any, index: number) => {
                return (
                  <DrawerCart
                    updateCard={updateCardAdd}
                    Cart={cart}
                    smallButton={true}
                    action="add"
                    key={index}
                  />
                );
              })}
            </div>
            <div className="drawer__price">Сума 898$</div>
            <div className="drawer__description">
              Додай бокс для вау ефекту! ✨⬇️
            </div>
            <div className="drawer__row">
              <input
                id="drawer-box-checkbox1"
                className="drawer__box-checkbox"
                type="radio"
                name="box"
              />
              <label htmlFor="drawer-box-checkbox1" className="drawer__box">
                <img
                  className="drawer__box-img"
                  src={`${process.env.PUBLIC_URL}/img/carts/low_price.jpg`}
                />
                <h5 className="drawer__box-title">Rafaello Box</h5>
              </label>
              <input
                id="drawer-box-checkbox2"
                className="drawer__box-checkbox"
                type="radio"
                name="box"
              />
              <label htmlFor="drawer-box-checkbox2" className="drawer__box">
                <img
                  src={`${process.env.PUBLIC_URL}/img/carts/low_price.jpg`}
                  className="drawer__box-img"
                />
                <h5 className="drawer__box-title">Rafaello Box</h5>
              </label>
              <input
                id="drawer-box-checkbox3"
                className="drawer__box-checkbox"
                type="radio"
                name="box"
              />
              <label htmlFor="drawer-box-checkbox3" className="drawer__box">
                <img
                  src={`${process.env.PUBLIC_URL}/img/carts/low_price.jpg`}
                  className="drawer__box-img"
                />
                <h5 className="drawer__box-title">Rafaello Box</h5>
              </label>
            </div>
            <h4 className="drawer__text">Прізвище та Імя:</h4>
            <input
              className="drawer__input"
              type="text"
              placeholder="Прізвище Імя По-батькові"
            />
            <h4 className="drawer__text">Телефон:</h4>
            <input
              className="drawer__input"
              type="text"
              placeholder="+380-99-999-99-99"
              value={number}
              onChange={(e) => setNumber(e.target.value)}
            />

            <h4 className="drawer__text">Місто:</h4>
            <input
              className="drawer__input"
              type="text"
              placeholder="м. Київ"
            />
            <h4 className="drawer__text">Відділення Нової Пошти:</h4>
            <input className="drawer__input" type="text" placeholder="№1" />
            <h4 className="drawer__text">Коментар:</h4>
            <input
              className="drawer__input"
              type="text"
              placeholder="Залиште комендар до свого замовлення"
            />
            <h4 className="drawer__text">Як з вами звязатись</h4>
            <label className="drawer__label">
              <input
                className="drawer__label-input"
                type="radio"
                name="connection"
              />
              <h5 className="drawer__label-title">Viber</h5>
            </label>
            <label className="drawer__label">
              <input
                className="drawer__label-input"
                type="radio"
                name="connection"
              />
              <h5 className="drawer__label-title">Telegram</h5>
            </label>
            <h4 className="drawer__text">Спосіб оплати</h4>
            <label className="drawer__label">
              <input className="drawer__label-input" type="radio" name="pay" />
              <h5 className="drawer__label-title">Оплата онлайн з monopay</h5>
            </label>
            <label className="drawer__label">
              <input className="drawer__label-input" type="radio" name="pay" />
              <h5 className="drawer__label-title">При отриманні на пошті</h5>
            </label>
            <div className="drawer__price">Сума 898$</div>
            <button className="drawer__button">Оформити замовлення</button>
            <p className="drawer__smallText">
              Натискаючи кнопку, ви погоджуєтесь з
              <span>Політикою конфіденційності</span> та
              <span>Договором публічної оферти</span>
            </p>
          </>
        ) : (
          <div className="like__nothing">
            <h2 className="like__text">Ви ще нічого не замовили</h2>
            <button
              className="like__button"
              onClick={() => setOpenBasket && setOpenBasket(!isBasketOpen)}
            >
              ЗАМОВИТИ
            </button>
          </div>
        )}
      </div>
    </section>
  );
};

const LikeButton: React.FC<Like> = ({
  counter,
  classeForButton,
  likeMock,
  setOpenLike,
}) => {
  return (
    <button
      className={`LikeButton`}
      style={counter > 0 ? { opacity: "1" } : { opacity: "0" }}
      onClick={() => setOpenLike(true)}
    >
      <div className={`LikeButton__wrapper ${classeForButton}`}>
        <img
          src={`${process.env.PUBLIC__URL}/img/shopping-bag-shopper-svgrepo-com.svg`}
        />
      </div>
      <span className="header__mobile_number header__mobile_number_blue">
        {counter}
      </span>
    </button>
  );
};

const AddButton: React.FC<Add> = ({ counter, classeForButton, setOpenAdd }) => {
  return (
    <button
      className={`AddButton`}
      style={counter > 0 ? { opacity: "1" } : { opacity: "0" }}
      onClick={() => setOpenAdd(true)}
    >
      <div className={`AddButton__wrapper ${classeForButton}`}>
        <svg
          role="img"
          className="t706__carticon-img"
          xmlns="http://www.w3.org/2000/svg"
          viewBox="0 0 64 64"
          fill="#fff"
        >
          <path
            fill="none"
            stroke-width="2"
            stroke-miterlimit="10"
            d="M44 18h10v45H10V18h10z"
          ></path>
          <path
            fill="none"
            stroke-width="2"
            stroke-miterlimit="10"
            d="M22 24V11c0-5.523 4.477-10 10-10s10 4.477 10 10v13"
          ></path>
        </svg>
      </div>
      <span className="header__mobile_number header__mobile_number_red">
        {counter}
      </span>
    </button>
  );
};

const Header: React.FC<header> = ({
  likeMock,
  updateCardLike,
  addMock,
  updateCardAdd,
  counterLike,
  counterAdd,
}) => {
  const [isMenuOpen, setOpenMenu] = useState(false);
  const [isBasketOpen, setOpenBasket] = useState(false);
  const [isLikeOpen, setOpenLike] = useState(false);
  const [classeLike, setLikeClass] = useState("LikeButton_animation");
  const [classeAdd, setAddClass] = useState("LikeButton_animation");

  useEffect(() => {
    setLikeClass("");
    setTimeout(() => {
      setLikeClass("LikeButton_animation");
    }, 10);
  }, [counterLike]);

  useEffect(() => {
    setAddClass("");
    setTimeout(() => {
      setAddClass("LikeButton_animation");
    }, 10);
  }, [counterAdd]);

  isBasketOpen || isMenuOpen || isLikeOpen
    ? (document.body.style.overflow = "hidden")
    : (document.body.style.overflow = "auto");

  const closeMenu = (
    event: React.MouseEvent<HTMLButtonElement | HTMLDivElement>
  ): void => {
    if (event.currentTarget.classList[0] === "header__menu") {
      setOpenMenu(false);
    } else {
      console.log("No");
    }
  };
  return (
    <>
      <LikeButton
        classeForButton={classeLike}
        counter={counterLike}
        likeMock={likeMock}
        setOpenLike={setOpenLike}
      />
      <AddButton
        classeForButton={classeAdd}
        counter={counterAdd}
        setOpenAdd={setOpenBasket}
      />
      <LikedCart
        updateCardLike={updateCardLike}
        carts={likeMock}
        isLikeOpen={isLikeOpen}
        setOpenLike={setOpenLike}
      />
      <Basket
        updateCardAdd={updateCardAdd}
        carts={addMock}
        isBasketOpen={isBasketOpen}
        setOpenBasket={setOpenBasket}
      />
      <header className="header">
        <Link to={"/"} onClick={() => setOpenMenu(false)}>
          <div className="header__logo">
            <img
              src={`${process.env.PUBLIC_URL}/img/logo.svg`}
              className="header__img"
            />
          </div>
        </Link>
        <div
          onClick={(
            e: React.MouseEvent<HTMLButtonElement | HTMLDivElement>
          ): void => closeMenu(e)}
          className={
            isMenuOpen ? " header__menu header__menu--active" : "header__menu "
          }
        >
          <div
            className={
              isMenuOpen
                ? "header__wrapper header__wrapper--active"
                : "header__wrapper"
            }
          >
            <ul className="header__menu-block">
              <Link
                to={"/cupsWidthPhotos"}
                onClick={() => setOpenMenu(false)}
                className="header__menu-block-link"
              >
                Чашки з фото
              </Link>
              <Link
                to={"/chameleon"}
                onClick={() => setOpenMenu(false)}
                className={`header__menu-block-link`}
              >
                Чашки з хамелеон
              </Link>
              <Link
                to={"/BisSize"}
                onClick={() => setOpenMenu(false)}
                className="header__menu-block-link"
              >
                Big Size
              </Link>
              <Link
                to={"/Boxs"}
                onClick={() => setOpenMenu(false)}
                className="header__menu-block-link"
              >
                Бокси
              </Link>
              <Link
                to={"/Delivery"}
                onClick={() => setOpenMenu(false)}
                className="header__menu-block-link"
              >
                Доставка
              </Link>
              <Link
                to={"/AboutUs"}
                onClick={() => setOpenMenu(false)}
                className="header__menu-block-link"
              >
                Про нас
              </Link>
            </ul>
            <p className="header__menu-description">
              Mr.Gifter- найдивовижніші подарунки!
            </p>
            <div className="header__menu-social-block">
              <img
                className="header__menu-social-img"
                src={`${process.env.PUBLIC_URL}/img/instagram-svgrepo-com.svg`}
              />
              <img
                className="header__menu-social-img"
                src={`${process.env.PUBLIC_URL}/img/telegram-svgrepo-com.svg`}
              />
              <img
                src={`${process.env.PUBLIC_URL}/img/img/viber-chat-app-svgrepo-com.svg`}
                className="header__menu-social-img"
              />
            </div>
          </div>
        </div>
        <div className="header__mobile">
          <div
            className={`header__mobile-like header__mobile-link `}
            onClick={() => setOpenLike((status) => !status)}
          >
            {likeMock.length > 0 ? (
              <span className="header__mobile_number header__mobile_number_blue">
                {likeMock.length}
              </span>
            ) : null}

            <img
              className={classeLike}
              src={`${process.env.PUBLIC_URL}/img/love-svgrepo-com.svg`}
            />
          </div>
          <div
            className="header__mobile-cart header__mobile-link"
            onClick={() => setOpenBasket((status) => !status)}
          >
            {addMock.length ? (
              <span className="header__mobile_number header__mobile_number_red">
                {addMock.length}
              </span>
            ) : null}

            <img
              className={classeAdd}
              src={`${process.env.PUBLIC_URL}/img/cart-svgrepo-com.svg`}
            />
          </div>
          <div
            className={
              isMenuOpen
                ? "header__mobile-button header__mobile-link header__mobile-button--active"
                : "header__mobile-button header__mobile-link"
            }
            onClick={() => setOpenMenu((status) => !status)}
          >
            <span></span>
            <span></span>
            <span></span>
          </div>
        </div>
      </header>
    </>
  );
};

export default Header;
