import React, { useEffect, useState } from "react";
import style from "./style.module.scss";

interface PropTypes {
  cart: any;
  updateCardLike(updateCardLike: any): any;
  updateCardAdd(updateCardAdd: any): any;
  onLikeClick?: any;
  carts?: any;
  changedState: any;
  setLikeCarts: any;
}

const Cart: React.FC<PropTypes> = ({
  setLikeCarts,
  changedState,
  onLikeClick,
  cart,
}) => {
  const [firstImg, secondImg] = cart.images;
  const [card, setCard] = useState({
    name: "",
    price: null,
    oldPrice: null,
    isLike: false,
    isAdd: false,
    redText: "",
  });
  useEffect(() => {
    setCard(cart);
  }, []);

  const fileterLike = (updatedCart: any) => {
    if (updatedCart.isLike) {
      setLikeCarts((prevData: any) => [...prevData, updatedCart]);
    } else {
      setLikeCarts((prevData: any) =>
        prevData.filter((cart: any) => cart.id !== updatedCart.id)
      );
    }
  };

  return (
    <>
      <div className={style.slider__cart}>
        <div className={style.slider__cartImgBlock}>
          <img
            loading="lazy"
            className={style.slider__cartImgFirst}
            src={`${process.env.PUBLIC_URL}/img/${firstImg}`}
          />

          <img
            className={style.slider__cartImgSecond}
            loading="lazy"
            src={`${process.env.PUBLIC_URL}/img/${firstImg}`}
          />
        </div>
        <h3 className={style.slider__cartTitle}>{card.name}</h3>
        <div className={style.slider__cartPrice}>
          <span className={style.slider__cartPriceCurrent}>{card.price}$</span>
          <span className={style.slider__cartPriceOld}>{card.oldPrice}$</span>
        </div>
        <button
          className={style.slider__cartButton}
          onClick={() => console.log("add")}
        >
          Додати в кошик
        </button>
        <button
          className={
            card.isLike
              ? `${style.slider__cartLike} ${style.slider__cartLike_active}`
              : `${style.slider__cartLike}`
          }
          onClick={(e) => onLikeClick(e, card, setCard, changedState(carts))}
        >
          <svg
            width="26"
            height="23"
            viewBox="0 0 26 23"
            fill="none"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path
              d="M24.3095 7.82226C24.3095 14.1423 13 20.8677 13 20.8677C13 20.8677 1.69043 14.1423 1.69043 7.82226C1.69043 -0.758756 13 -0.642878 13 6.90926C13 -0.642878 24.3095 -0.529907 24.3095 7.82226Z"
              stroke="black"
              strokeWidth="1.5"
              strokeLinejoin="round"
            />
          </svg>
        </button>
        {card.redText.length > 2 ? (
          <span
            dangerouslySetInnerHTML={{ __html: card.redText }}
            className={style.slider__cartButtonRed}
          >
            {}
          </span>
        ) : null}
      </div>
    </>
  );
};

export default Cart;
