import React, { useState } from "react";
import style from "./style.module.scss";
import axios from "axios";

interface PropTypes {
  cart: any;
  updateCard(updateCard: any): any;
}

const Cart: React.FC<PropTypes> = ({ cart, updateCard }) => {
  const [firstImg, secondImg] = cart.images;
  const [card, setCard] = useState(null);
  const handleLikeClick = (event: React.MouseEvent<HTMLButtonElement>) => {
    // Оновити карточку та викликати функцію оновлення в CardList
    event.preventDefault();

    const updatedCart = { ...cart, isLike: !cart.isLike };
    setCard(updatedCart);
    updateCard(updatedCart);
    // Відправлення запиту на сервер для збереження змін в базі даних
    axios
      .put(
        `https://656293beee04015769a680af.mockapi.io/data/${updatedCart.id}`,
        {
          isLike: updatedCart.isLike,
        }
      )
      .then((response) => console.log("Like status updated successfully"))
      .catch((error) => console.error("Error updating like status:", error));
  };
  return (
    <div className={style.slider__cart}>
      <div className={style.slider__cartImgBlock}>
        <img
          className={style.slider__cartImgFirst}
          src={`${process.env.PUBLIC_URL}/img/carts/${firstImg}`}
        />

        <img
          className={style.slider__cartImgSecond}
          src={`${process.env.PUBLIC_URL}/img/carts/${secondImg}`}
        />
      </div>
      <h3 className={style.slider__cartTitle}>{cart.name}</h3>
      <div className={style.slider__cartPrice}>
        <span className={style.slider__cartPriceCurrent}>{cart.price}$</span>
        <span className={style.slider__cartPriceOld}>{cart.oldPrice}$</span>
      </div>
      <button
        className={style.slider__cartButton}
        onClick={() => console.log("click add")}
      >
        Додати в кошик
      </button>
      <button
        className={
          cart.isLike
            ? `${style.slider__cartLike} ${style.slider__cartLike_active}`
            : `${style.slider__cartLike}`
        }
        onClick={handleLikeClick}
      >
        <svg
          width="26"
          height="23"
          viewBox="0 0 26 23"
          fill="none"
          xmlns="http://www.w3.org/2000/svg"
        >
          <path
            d="M24.3095 7.82226C24.3095 14.1423 13 20.8677 13 20.8677C13 20.8677 1.69043 14.1423 1.69043 7.82226C1.69043 -0.758756 13 -0.642878 13 6.90926C13 -0.642878 24.3095 -0.529907 24.3095 7.82226Z"
            stroke="black"
            stroke-width="1.5"
            stroke-linejoin="round"
          />
        </svg>

        {/* <img src={`${process.env.PUBLIC_URL}/img/heart-svgrepo-com.svg`} /> */}
      </button>
      <span className={style.slider__cartButtonRed}>{cart.redText}</span>
    </div>
  );
};

export default Cart;
