import { Route, Routes } from "react-router-dom";

import Header from "./components/header";
import Footer from "./components/footer";
import Feedback from "./components/feedback";
import Autorization from "./pages/autorization";
import PageTemplate from "./pages/PageTemplate";
import Search from "./components/search";
import CartPage from "./pages/CartPage";
import HomePage from "./pages/HomePage";

import "./style.scss";
import "swiper/css";
import "swiper/css/navigation";
import "swiper/css/pagination";
import { useEffect, useState } from "react";
import axios from "axios";

const App = () => {
  const [token, setToken] = useState("");
  const [mockData, setData] = useState([]);
  const [likeMock, setLikeMock] = useState([]);
  const [addMock, setAddMock] = useState([]);

  useEffect(() => {
    axios
      .get("https://65646353ceac41c0761e1278.mockapi.io/data")
      .then((response) => setData(response.data))
      .then((data) => {
        const filterLikedItems = (items: any) => {
          return items.filter((item: any) => item.isLike);
        };
        setLikeMock(filterLikedItems(mockData));
      })
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  useEffect(() => {
    const filterLikedItems = (items: any) => {
      return items.filter((item: any) => item.isLike);
    };
    setLikeMock(filterLikedItems(mockData));
    console.log(likeMock);
  }, [mockData]);

  const updateCard = (updatedCard: any) => {
    // Оновити стан списку карт з оновленою карточкою
    setData((prevData: any) =>
      prevData.map((card: any) =>
        card.id === updatedCard.id ? updatedCard : card
      )
    );
  };

  return (
    <div className="App">
      <Header likeMock={likeMock} updateCard={updateCard} />
      <Routes>
        <Route
          path=""
          element={<HomePage updateCard={updateCard} data={mockData} />}
        />
        <Route
          path="/cart/:id"
          element={
            <CartPage
              cards={mockData}
              setData={setData}
              updateCard={updateCard}
              like={likeMock}
              setLikeMock={setLikeMock}
              addMock={addMock}
              setAddMock={setAddMock}
            />
          }
        />
        <Route
          path="autorization"
          element={<Autorization token={token} setToken={setToken} />}
        />
        <Route
          path="registration"
          element={<Autorization token={token} setToken={setToken} />}
        />
        <Route
          path="cupsWidthPhotos"
          element={
            <PageTemplate
              data={mockData}
              isShowCarts={true}
              isShowLables={false}
              isShowText={false}
              updateCard={updateCard}
            />
          }
        />
        <Route
          path="chameleon"
          element={
            <PageTemplate
              updateCard={updateCard}
              data={mockData}
              isShowCarts={true}
              isShowLables={false}
              isShowText={false}
            />
          }
        />
        <Route
          path="BisSize"
          element={
            <PageTemplate
              updateCard={updateCard}
              data={mockData}
              isShowCarts={true}
              isShowLables={false}
              isShowText={false}
            />
          }
        />
        <Route
          path="Boxs"
          element={
            <PageTemplate
              data={mockData}
              isShowCarts={true}
              isShowLables={true}
              isShowText={false}
              updateCard={updateCard}
            />
          }
        />
        <Route
          path="Delivery"
          element={
            <PageTemplate
              updateCard={updateCard}
              data={mockData}
              isShowCarts={false}
              isShowLables={false}
              isShowText={true}
              title="Про доставку"
              description="Намагаємось доставити ваше замовлення так швидко, як котик біжить як чує звук відкриття холодильнику.
              Оформлені до 18:00 замовлення відправляємо в той же день!
              Доставка Новою Поштою 1-3 дні. Оплатити можна при отриманні на пошті, або по передплаті на карту."
            />
          }
        />
        <Route
          path="AboutUs"
          element={
            <PageTemplate
              updateCard={updateCard}
              data={mockData}
              isShowCarts={false}
              isShowLables={false}
              isShowText={true}
              title="Про нас"
              description="Колись я оновлю цей блок і тут буде інформація про команду і те що ми зробили за весь час."
            />
          }
        />
      </Routes>

      <Feedback />
      <Search />
      <Footer />
    </div>
  );
};

export default App;
