import React, { ReactNode, useEffect, useState } from "react";
import { Link, useLocation } from "react-router-dom";
import Cart from "src/components/cart";
import QuestionLabel from "src/components/question";

interface PropsType {
  data: Array<any>;
  isShowLables: boolean;
  isShowText: boolean;
  isShowCarts: boolean;
  title?: string;
  description?: string;
  updateCardLike: any;
  updateCardAdd: any;
}
const PageTemplate: React.FC<PropsType> = ({
  data,
  isShowLables,
  isShowText,
  isShowCarts,
  title,
  description,
  updateCardLike,
  updateCardAdd,
}) => {
  const [location, setLocation] = useState("");
  const Location = useLocation();
  const [labelDescription, seDescription] = useState("");

  useEffect(() => {
    window.scrollTo(0, 0);
    setLocation(Location.pathname);
    seDescription(data[0].about);
  }, []);

  const pathToImg = (location: string) => {
    return location === "/Boxs"
      ? `${process.env.PUBLIC_URL}/img/boxs/`
      : `${process.env.PUBLIC_URL}/img/carts/`;
  };

  const showCarts = (): ReactNode[] => {
    return data.map((cart, index) => (
      <Link to={`/cart/${cart.id}`} key={cart.id}>
        <Cart
          pathToImg={pathToImg(location)}
          updateCardAdd={updateCardAdd}
          cart={cart}
          updateCardLike={updateCardLike}
        />
      </Link>
    ));
  };

  return (
    <>
      <div className="page__wrapper">{isShowCarts ? showCarts() : null}</div>
      <div className="page__container">
        {isShowLables ? (
          <>
            <QuestionLabel
              title="Детальніше про продукт"
              description={labelDescription}
            />
            <QuestionLabel
              title="Доставка і оплата"
              description={`
        Оформлені до 18:00 замовлення відправляємо в той же день!<br/>
        <br/>
        Доставка Новою Поштою 1-3 дні`}
            />
          </>
        ) : null}
        {isShowText ? (
          <>
            <h2 className="page__title">{title}</h2>
            <p className="page__description">{description}</p>
          </>
        ) : null}
      </div>
    </>
  );
};
export default PageTemplate;
