import { Swiper, SwiperSlide } from "swiper/react";
import { Navigation, Pagination, Autoplay, Keyboard } from "swiper/modules";
import style from "./feedback.module.scss";
import React, { useCallback, useRef } from "react";

const Feedback = () => {
  const navigationPrevRef = useRef(null);
  const navigationNextRef = useRef(null);
  const sliderRef = useRef(null);

  const handlePrev = useCallback(() => {
    if (!sliderRef.current) return;
    sliderRef.current.swiper.slidePrev();
  }, []);

  const handleNext = useCallback(() => {
    if (!sliderRef.current) return;
    sliderRef.current.swiper.slideNext();
  }, []);

  return (
    <section className={style.feedback}>
      <h2 className={`title ${style.feedback__title} `}>
        Відгуки наших клієнтів
      </h2>
      <Swiper
        className={style.feedback__slider}
        modules={[Navigation, Pagination, Autoplay, Keyboard]}
        slidesPerView={1}
        spaceBetween={50}
        navigation={{
          nextEl: `${style.swiper_button_next}`,
          prevEl: `${style.swiper_button_prev}`,
        }}
        loop={true}
        keyboard={{ enabled: true }}
        autoplay={{ delay: 5000 }}
        pagination={{ clickable: true }}
        onBeforeInit={(swiper: any) => {
          swiper.params.navigation.prevEl = navigationPrevRef.current;
          swiper.params.navigation.nextEl = navigationNextRef.current;
        }}
      >
        <SwiperSlide className={style.feedback__slide}>
          <img src={`${process.env.PUBLIC_URL}/img/feedback/feedback1.jpeg`} />
        </SwiperSlide>
        <SwiperSlide className={style.feedback__slide}>
          <img src={`${process.env.PUBLIC_URL}/img/feedback/feedback1.jpeg`} />
        </SwiperSlide>
        <SwiperSlide className={style.feedback__slide}>
          <img src={`${process.env.PUBLIC_URL}/img/feedback/feedback1.jpeg`} />
        </SwiperSlide>
        <SwiperSlide className={style.feedback__slide}>
          <img src={`${process.env.PUBLIC_URL}/img/feedback/feedback1.jpeg`} />
        </SwiperSlide>
        <div className={style.swiper_button_next} onClick={handlePrev}></div>
        <div className={style.swiper_button_prev} onClick={handleNext}></div>
      </Swiper>
    </section>
  );
};
export default Feedback;
