import React, { useState } from "react";

interface PaymentRequest {
  amount: number;
  description: string;
  accountId: string; // Your Monobank account ID
  apiKey: string; // Your Monobank API key
}

const PaymentComponent: React.FC = () => {
  const [paymentRequest, setPaymentRequest] = useState<PaymentRequest>({
    amount: 100,
    description: "Service payment",
    accountId: "YOUR_ACCOUNT_ID",
    apiKey: "utR_bnF6LUzdc4pr3yFNFF2kKEPk75xeIlItZx9QfaxY",
  });

  const handlePayment = async () => {
    try {
      const response = await initiatePayment(paymentRequest);
      console.log("Payment initiated successfully:", response);
    } catch (error) {
      console.error("Error initiating payment:", error);
    }
  };

  const initiatePayment = async (request: PaymentRequest) => {
    const apiUrl = "https://api.monobank.ua/personal/auth/registration";

    const requestOptions: RequestInit = {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "X-Token": request.apiKey,
      },
      body: JSON.stringify({
        pubkey: "LS0tLS1CRUdJTiBQVUJMSUMgS0VZLS0tLS0KTUZZd0VBWUhLb1pJemow...",
        name: "Test",
        description:
          "Ми робимо найрозумніший PFM з усіх можливих і нам потрібен доступ до виписка користувача, щоб створювати красиву статистику",
        contactPerson: "Іван Калинич",
        phone: "380934638895",
        email: "ivan.kalunuch12@gmail.com",
        logo: "iVBORw0KGgoAAAANSUhEUgAAAUAAAACECAYAAADhnvK8AAAapElEQVR42...",
      }),
    };
    const response = await fetch(apiUrl, requestOptions);

    if (!response.ok) {
      throw new Error(`Payment initiation failed: ${response.statusText}`);
    }

    return response.json();
  };

  return (
    <div style={{ marginTop: "100px" }}>
      <h1>Payment Page</h1>
      <p>Amount: {paymentRequest.amount} UAH</p>
      <button onClick={handlePayment}>Pay Now</button>
    </div>
  );
};

export default PaymentComponent;
