import React, { useEffect, useState } from "react";
import { Link, useAsyncValue } from "react-router-dom";
import Questions from "src/components/questions";
import ImageModal from "src/components/modalImg";
import { Link as ScrollLink } from "react-scroll";

interface InputData {
  type: {
    value: string;
    isValid: boolean;
    errorMessage: string;
  };
  text: {
    value: string;
    isValid: boolean;
    errorMessage: string;
  };
  photos: {
    value: any;
    isValid: boolean;
    errorMessage: string;
  };
  phone: {
    value: string;
    isValid: boolean;
    errorMessage: string;
  };
  comment: {
    value: string;
    isValid: boolean;
    errorMessage: string;
  };
}

const CustomPage = () => {
  const [basketBox, setBasketBox] = useState([]);
  const [typeOfCups, setTypeOfCups] = useState([]);

  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedImageUrl, setSelectedImageUrl] = useState("");
  const [file, setFile] = useState([]);

  const [inputData, setInputData] = useState<InputData>({
    type: {
      value: "",
      isValid: true,
      errorMessage: "",
    },
    text: {
      value: "",
      isValid: true,
      errorMessage: "",
    },
    phone: {
      value: "",
      isValid: true,
      errorMessage: "",
    },
    photos: {
      value: "",
      isValid: true,
      errorMessage: "",
    },
    comment: {
      value: "",
      isValid: true,
      errorMessage: "",
    },
  });

  useEffect(() => {
    fetch(`${process.env.PUBLIC_URL}/data/basket.json`)
      .then((response) => response.json())
      .then((data) => setBasketBox(data))
      .catch(() => console.log("basket not find"));

    fetch(`${process.env.PUBLIC_URL}/data/typeOfCups.json`)
