import React, {
  useEffect,
  useState,
  ChangeEvent,
  KeyboardEvent,
  useRef,
} from "react";
import DrawerCart from "../drawerCart";
import Cart from "../cart";
import { Link } from "react-router-dom";
interface PropsType {
  isLikeOpen?: boolean | undefined;
  setOpenLike?: (status: boolean) => void;
  isBasketOpen?: boolean | undefined;
  setOpenBasket?: (status: boolean) => void;
  updateCardLike?: any;
  updateCardAdd?: any;
  carts?: [];
  onLike: any;
  increment: any;
  decrement: any;
  setBoxsPriceBasket: any;
  removeAdd: any;
  price: number;
  boxsPriceBasket: any;
  setAddedCarts: any;
}

interface array {
  name: string;
  price: number;
  img: string;
}

const Basket: React.FC<PropsType> = ({
  setAddedCarts,
  removeAdd,
  decrement,
  increment,
  isBasketOpen,
  onLike,
  setBoxsPriceBasket,
  setOpenBasket,
  price,
  carts,
  boxsPriceBasket,
}) => {
  const [basketBox, setBasketBox] = useState([]);
  const [inputName, setInputName] = useState("");
  const [inputPhone, setInputPhone] = useState("");
  const [inputCity, setInputCity] = useState("");
  const [inputDepartments, setInputDepartments] = useState("");
  const [inputComment, setInputComent] = useState("");
  const [inputMessenger, setInputMessenger] = useState("viber");
  const [inputPayment, setInputPayment] = useState("monopay");
  const [formStatus, setFormStatus] = useState(false);

  const [isValid, setIsValid] = useState<boolean>(true);
  const inputRef = useRef<HTMLInputElement>(null);
  const [phoneNumber, setPhoneNumber] = useState<string>("");

  const formatPhoneNumber = (input: string): string => {
    let formattedInput = input.trim();

    if (formattedInput.startsWith("0")) {
      formattedInput = `+38 ${formattedInput.slice(1)}`;
    } else if (formattedInput.startsWith("380")) {
      formattedInput = `+${formattedInput.slice(3)}`;
    } else if (formattedInput.startsWith("+")) {
      formattedInput = formattedInput.slice(1);
    }

    return formattedInput.replace(
      /(\d{3})(\d{3})(\d{2})(\d{2})/,
      "+38 ($1) $2-$3-$4"
    );
  };

  const handleInputChange = (event: ChangeEvent<HTMLInputElement>) => {
    const input = event.target.value;
    const formattedInput = formatPhoneNumber(input);

    setIsValid(formattedInput.length >= 18); // Довжина українського номера
    setPhoneNumber(formattedInput);
  };

  const handleKeyDown = (event: KeyboardEvent<HTMLInputElement>) => {
    // Збереження поточної позначки (cursor position) при натисканні клавіші
    if (inputRef.current) {
      inputRef.current.selectionStart = inputRef.current.selectionEnd =
        event.currentTarget.selectionStart;
    }
  };

  const handleKeyPress = (event: KeyboardEvent<HTMLInputElement>) => {
    // Ігнорування всіх не-цифрових символів
    const digitRegex = /\d/;

    if (!digitRegex.test(event.key) && event.key !== "Backspace") {
      event.preventDefault();
    }
  };

  useEffect(() => {
    fetch(`${process.env.PUBLIC_URL}/data/basket.json`)
      .then((response) => response.json())
      .then((data) => setBasketBox(data))
      .catch(() => console.log("basket not find"));
  }, []);

  const sendMessageToTelegram = async (data: any) => {
    setFormStatus(false);

    const TOKEN = "6619280299:AAGIL6f6uD5nOU1Sjw26zzqvyI0V_fZZKq0";
    const CHAT_ID = "-1002007095666";
    const URL_API = `https://api.telegram.org/bot${TOKEN}`;

    const sendText = () => {
      const getCurrentDate = new Date().toISOString().split("T")[0];
      //preparing text for post to telegram
      let message = `<b>Заявка з сайта! </b>
      `;

      message += `
Дата створення замовлення: <i>${getCurrentDate}</i> 
Імя замовника <i>${inputName}</i>
Номер телефону <i>${inputPhone}</i>
Адреса на яку відправити:
<i>Місто ${inputCity},
Номер віділення ${inputDepartments}</i>
Коментар <i>${inputComment}</i>

<b>Замовлення</b>
Загальна сума : <i>${price} грн</i>

<b>Карточки</b>`;
      data.forEach((element: any) => {
        message += `
<b>${element.name} </b>:
<b>Тип чашки </b> <i>${
          element.type.length > 0
            ? element.type.filter((element: any) => element.isActive)[0].name
            : "Без типа"
        }</i>
Ціна за чашку:  <i>${element.price}</i> гривень
Кількість чашок: <b>${element.count}</b>
`;
      });
      message += `
<b>Додатковий бокс, ціна:</b> <i>
${boxsPriceBasket > 0 ? boxsPriceBasket : "Бокс не був вибраний"}</i>
Спосіб оплати ${inputPayment} 
Спосіб звязку ${inputMessenger}`;

      //preparing sending data
      const formData = new URLSearchParams();
      formData.append("chat_id", CHAT_ID);
      formData.append("text", message);
      formData.append("parse_mode", "html");

      //sending data
      fetch(`${URL_API}/sendMessage`, {
        method: "POST",
        body: formData,
      })
        .then((response) => {
          return response;
        })
        .then((response) => response.json())
        .then((data) => {
          console.log("Message sent:", data);
        })
        .catch((error) => {
          console.error("Error:", error);
        });
    };
    sendText();
    setInputName("");
    setInputPhone("");
    setInputCity("");
    setInputDepartments("");
    setInputComent("");
    setAddedCarts([]);
  };

  const sendMessageToBackEnd = (orderedCarts: any) => {
    const getCurrentDate = new Date().toISOString().split("T")[0];

    let filterCarts: any = [];
    filterCarts = orderedCarts.map((element: any, index: any) => {
      const activeType =
        element.type.length > 0
          ? element.type.filter((type: any) => type.isActive)
          : [];

      return {
        price: element.price, // Замініть на значення з форми або іншого джерела
        quantity: element.count,
        name: element.name, // Замініть на значення з форми або іншого джерела
        picture: element.images[0], // Замініть на значення з форми або іншого джерела
        properties:
          activeType.length > 0
            ? [
                {
                  name: "Тип чашки",
                  value: activeType[0].name,
                },
              ]
            : null,
      };
    });
    try {
      const activeBoxBasket = () => {
        return basketBox.filter((element: array) => {
          return element.price === boxsPriceBasket;
        });
      };
      const actBox: array = activeBoxBasket()[0];

      filterCarts.push({
        price: actBox.price, // Замініть на значення з форми або іншого джерела
        quantity: 1,
        name: actBox.name, // Замініть на значення з форми або іншого джерела
        picture: actBox.img, // Замініть на значення з форми або іншого джерела
        properties: [],
      });
    } catch {
      console.log("basket is not choose");
    }

    const dataForBack = {
      source_id: 2,
      client_comment: inputComment,
      buyer: {
        full_name: `${inputName}`, // Замініть на значення з форми або іншого джерела
        // email: "john.doe@example.com", // Замініть на значення з форми або іншого джерела
        phone: `${inputPhone}`, // Замініть на значення з форми або іншого джерела
      },
      shipping: {
        shipping_address_city: `${inputCity}`, // Замініть на значення з форми або іншого джерела
        shipping_address_region: `${inputDepartments}`, // Замініть на значення з форми або іншого джерела
      },
      products: filterCarts,
      payments:
        inputPayment === "monopay"
          ? [
              {
                payment_method_id: 2,
                payment_method: "Monopay",
                amount: price,
                description: "Авансовий платіж",
                status: "not_paid",
              },
            ]
          : [
              {
                payment_method_id: 2,
                payment_method: "Postha",
                amount: price,
                description: "Авансовий платіж",
                status: "not_paid",
              },
            ],
    };

    fetch("https://bf7d-37-73-102-133.ngrok-free.app/send-to-keycrm", {
      method: "POST",
      mode: "cors",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
        "Cache-Control": "no-cache",
        Pragma: "no-cache",
      },
      body: JSON.stringify(dataForBack),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => console.log(data))
      .catch((error) => console.error("Error:", error));
  };
  const onSubmitFunc = (e: any) => {
    e.preventDefault();
    if (
      inputName.length < 4 ||
      inputCity.length < 2 ||
      inputPhone.length <= 10 ||
      inputDepartments.length < 1
    ) {
      setFormStatus(true);
      return;
    } else {
      if (inputPayment === "monopay") {
        getDataFromMonoUser(carts);
      }
      sendMessageToTelegram(carts);
      sendMessageToBackEnd(carts);
    }
  };

  const dataLenght = carts?.length;

  const getDataFromMonoUser = (orderedCarts: any) => {
    const dataAboutCartForMono = orderedCarts.map(
      (cart: any, index: number) => {
        return {
          name: cart.name,
          qty: cart.count,
          sum: cart.price * 100,
          icoun: `${process.env.PUBLIC_URL}/img/${cart.images[0]}`,
          unit: "шт.",
          barcode: "string",
          header: "string",
          footer: "string",
          tax: [],
          uktzed: "string",
          discounts: [
            {
              type: "DISCOUNT",
              mode: "VALUE",
              value: cart.oldPrice,
            },
          ],
        };
      }
    );

    try {
      const activeBoxBasket = () => {
        return basketBox.filter((element: array) => {
          console.log(element.price, boxsPriceBasket);
          return element.price === boxsPriceBasket;
        });
      };

      const actBox: array = activeBoxBasket()[0];

      dataAboutCartForMono.push({
        name: actBox.name,
        qty: 1,
        sum: actBox.price * 100,
        icoun: `${process.env.PUBLIC_URL}/img/${actBox.img}}`,
        unit: "шт.",
        barcode: "string",
        header: "string",
        footer: "string",
        tax: [],
        uktzed: "string",
      });
    } catch {
      console.log("error");
    }
    fetch("https://api.monobank.ua/api/merchant/invoice/create", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "X-Token": "utR_bnF6LUzdc4pr3yFNFF2kKEPk75xeIlItZx9QfaxY",
      },
      body: JSON.stringify({
        amount: price * 100,
        redirectUrl: "https://mister-gifter.com/",
        webHookUrl: "https://bf7d-37-73-102-133.ngrok-free.app/webhook",
        merchantPaymInfo: {
          reference: "84d0070ee4e44667b31371d8f8813947",
          destination: "Подарунок від MISTER GIFTER",
          comment: "Подарунок від MISTER GIFTER",
          customerEmails: [
            "ivan.kalunuch12@gmail.com",
            "ivan.kalunuch31@gmail.com",
          ],
          paymentType: "debit",
          basketOrder: dataAboutCartForMono,
        },
      }),
    })
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        console.log(data);
        window.location.href = data.pageUrl;
      })
      .catch((response) => console.log(response));
  };

  const closeMenu = (event: React.MouseEvent<HTMLDivElement>): void => {
    const clickedElement = event.target as HTMLDivElement;
    if (clickedElement.className === "drawer drawer--active") {
      setOpenBasket ? setOpenBasket(false) : console.log("not foutn function");
    } else {
      console.log("No");
    }
  };

  return (
    <section
      className={isBasketOpen ? "drawer drawer--active" : "drawer"}
      onClick={(e: any) => closeMenu(e)}
    >
      <form className="drawer__wrapper">
        <h2 className="drawer__title">
          Ваше замовлення:{" "}
          <span
            className="drawer__exit"
            onClick={() => setOpenBasket && setOpenBasket(!isBasketOpen)}
          ></span>
        </h2>
        {dataLenght ? (
          <>
            <div className="drawer__carts-block">
              {carts.map((cart: any, index: number) => {
                return (
                  <DrawerCart
                    setOpenBasket={setOpenBasket}
                    removeAdd={removeAdd}
                    increment={increment}
                    onLike={onLike}
                    decrement={decrement}
                    Cart={cart}
                    smallButton={true}
                    action="add"
                    key={cart.id}
                  />
                );
              })}
            </div>
            <div className="drawer__price">Сума {price} ₴ </div>
            <Link
              onClick={() => setOpenBasket && setOpenBasket(!isBasketOpen)}
              to={"/Boxs"}
            >
              <div className="drawer__description">
                Додай бокс для вау ефекту! ✨⬇️
              </div>
            </Link>
            <div className="drawer__row">
              {basketBox.map((element: any, index: any) => {
                return (
                  <div key={index}>
                    <input
                      key={index}
                      id={`${element.name} ${index}`}
                      className="drawer__box-checkbox"
                      type="radio"
                      name="RADIOBOXS"
                    />
                    <label
                      onClick={() => setBoxsPriceBasket(element.price)}
                      key={index}
                      htmlFor={`${element.name} ${index}`}
                      className="drawer__box"
                    >
                      <img
                        src={`${process.env.PUBLIC_URL}/img/${element.img}`}
                        className="drawer__box-img"
                      />
                      <h5 className="drawer__box-title">{element.name}</h5>
                    </label>
                  </div>
                );
              })}
            </div>
            <h4 className="drawer__text">Прізвище та Імя:</h4>
            <input
              className="drawer__input"
              value={inputName}
              onChange={(e: any) => setInputName(e.target.value)}
              name="name"
              type="text"
              placeholder="Прізвище Імя По-батькові"
            />
            <h4 className="drawer__text">Телефон:</h4>
            <input
              className="drawer__input"
              ref={inputRef}
              type="text"
              value={phoneNumber}
              onChange={handleInputChange}
              onKeyDown={handleKeyDown}
              onKeyPress={handleKeyPress}
              placeholder="+38 (___) ___-__-__"
            />
            {!isValid && (
              <p style={{ color: "red" }}>Неприпустима довжина номера</p>
            )}
            {/* <input
              className="drawer__input"
              type="number"
              value={inputPhone}
              onChange={(e: any) => setInputPhone(e.target.value)}
              name="phone"
              placeholder="+380-99-999-99-99"
            /> */}

            <h4 className="drawer__text">Місто:</h4>
            <input
              name="City"
              className="drawer__input"
              type="text"
              value={inputCity}
              onChange={(e: any) => setInputCity(e.target.value)}
              placeholder="м. Київ"
            />
            <h4 className="drawer__text">Відділення Нової Пошти:</h4>
            <input
              value={inputDepartments}
              onChange={(e: any) => setInputDepartments(e.target.value)}
              className="drawer__input"
              type="text"
              placeholder="№1"
            />
            <h4 className="drawer__text">Коментар:</h4>
            <input
              value={inputComment}
              onChange={(e: any) => setInputComent(e.target.value)}
              name="Comment"
              className="drawer__input"
              type="text"
              placeholder="Залиште комендар до свого замовлення"
            />
            <h4 className="drawer__text">Як з вами звязатись</h4>
            <label className="drawer__label">
              <input
                className="drawer__label-input"
                type="radio"
                name="connection"
                value="viber"
                onClick={(e: any) => setInputMessenger(e.target.value)}
              />
              <h5 className="drawer__label-title">Viber</h5>
            </label>
            <label className="drawer__label">
              <input
                className="drawer__label-input"
                type="radio"
                onClick={(e: any) => setInputMessenger(e.target.value)}
                value="telegram"
                name="connection"
                checked
              />
              <h5 className="drawer__label-title">Telegram</h5>
            </label>
            <h4 className="drawer__text">Спосіб оплати</h4>
            <label className="drawer__label">
              <input
                onClick={(e: any) => setInputPayment(e.target.value)}
                checked
                className="drawer__label-input"
                type="radio"
                value="monopay"
                name="pay"
              />
              <h5 className="drawer__label-title">Оплата онлайн з monopay</h5>
            </label>
            <label className="drawer__label">
              <input
                className="drawer__label-input"
                onClick={(e: any) => setInputPayment(e.target.value)}
                type="radio"
                name="pay"
                value="novaposhta"
              />
              <h5 className="drawer__label-title">При отриманні на пошті</h5>
            </label>
            <div className="drawer__price">Сума {price} ₴</div>
            {formStatus ? (
              <div className="drawer__error">
                Будь ласка, заповніть всі обов'язкові поля
              </div>
            ) : null}
            <button
              className="drawer__button"
              type="submit"
              onClick={(e: any) => onSubmitFunc(e)}
            >
              Оформити замовлення
            </button>
            <p className="drawer__smallText">
              Натискаючи кнопку, ви погоджуєтесь з
              <span>Політикою конфіденційності</span> та
              <span>Договором публічної оферти</span>
            </p>
          </>
        ) : (
          <div className="like__nothing">
            <h2 className="like__text">Ви ще нічого не замовили</h2>
            <button
              className="like__button"
              onClick={() => setOpenBasket && setOpenBasket(!isBasketOpen)}
            >
              ЗАМОВИТИ
            </button>
          </div>
        )}
      </form>
    </section>
  );
};

export default Basket;
