import { Route, Routes } from "react-router-dom";
import React, { useEffect, useState } from "react";

import { observer } from "mobx-react-lite";
import myStore from "./store";

import Header from "./components/header";
import Footer from "./components/footer";
import Feedback from "./components/feedback";
import Autorization from "./pages/autorization/AuthPage";
import PageTemplate from "./pages/PageTemplate";
import Search from "./components/search";
import CartPage from "./pages/CartPage";
import HomePage from "./pages/HomePage";
import OnLikeText from "./components/onLikeText";

import "./style.scss";
import "swiper/css";
import "swiper/css/navigation";
import "swiper/css/pagination";

interface Cup {
  id: number;
  name: string;
  isLike: boolean;
  isAdd: boolean;
}

const App = () => {
  const [token, setToken] = useState("");
  const [mockData, setData] = useState([]);
  const [likedCart, setLikeCarts] = useState<Cup[]>([]);
  const [addMock, setAddCarts] = useState([]);
  const [counterLike, setLikeCouter] = useState(0);
  const [counterAdd, setAddCouter] = useState(0);
  const [mockBoxs, setMockBoxs] = useState([]);
  const [mockBigSize, setMockBigSize] = useState([]);
  const [mockChameleon, setMockChameleon] = useState([]);
  const [mockWithPhoto, setMockWithPhoto] = useState([]);
  const [mockMostPopular, setMockMostPopular] = useState([]);
  const [openedCart, setOpenedCart] = useState([]);

  useEffect(() => {
    const boxsesMock: [] = require("./data/boxs.json");
    const bigSizeMock: [] = require("./data/bigSize.json");
    const chameletonMock: [] = require("./data/chameleons.json");
    const withPhotoMock: [] = require("./data/cupsWithPhotos.json");
    setMockBoxs(boxsesMock);
    setMockBigSize(bigSizeMock);
    setMockChameleon(chameletonMock);
    setMockWithPhoto(withPhotoMock);
    setMockMostPopular([...withPhotoMock, ...chameletonMock, ...boxsesMock]);
  }, []);
  useEffect(() => {
    console.log(likedCart, mockWithPhoto);
  }, [likedCart, mockWithPhoto]);

  const updateCardsLike = (updatedCard: any, funcUpdateState: any) => {
    funcUpdateState((prevData: any) =>
      prevData.map((card: any) =>
        card.id === updatedCard.id ? updatedCard : card
      )
    );
  };

  const fileterLike = (updatedCart: any) => {
    if (updatedCart.isLike) {
      setLikeCarts((prevData: any) => [...prevData, updatedCart]);
    } else {
      setLikeCarts((prevData: any) =>
        prevData.filter((cart: any) => cart.id !== updatedCart.id)
      );
    }
  };

  const onLikeClick = (
    event: React.MouseEvent<HTMLButtonElement>,
    cart: any,
    setCart: any,
    chagedState: any
  ) => {
    event.preventDefault();
    const updatedCart = { ...cart, isLike: !cart.isLike };
    setCart(updatedCart);
    updateCardsLike(updateCardAdd, chagedState);
    fileterLike(updatedCart);
  };

  const updateCardAdd = (updatedCard: any) => {
    // Оновити стан списку карт з оновленою карточкою
    setData((prevData: any) =>
      prevData.map((card: any) =>
        card.id === updatedCard.id ? updatedCard : card
      )
    );
  };

  return (
    <div className="App">
      {likedCart.length > 0 ? (
        <OnLikeText name={likedCart[likedCart.length - 1].name} />
      ) : null}
      <Header
        counterAdd={counterAdd}
        counterLike={counterLike}
        updateCardAdd={updateCardAdd}
        addMock={addMock}
        likedCart={likedCart}
        updateCardsLike={updateCardsLike}
      />
      <Routes>
        <Route
          path=""
          element={
            <HomePage
              updateCardAdd={updateCardAdd}
              updateCardLike={updateCardsLike}
              mockBigSize={mockBigSize}
              mockBoxs={mockBoxs}
              mockMostPopular={mockMostPopular}
              setOpenedCart={setOpenedCart}
              onLikeClick={onLikeClick}
            />
          }
        />
        <Route
          path="/cart/:id"
          element={
            <CartPage
              cards={mockMostPopular}
              openedCart={openedCart}
              onLikeClick={onLikeClick}
              setOpenedCart={setOpenedCart}
              updateCardAdd={updateCardAdd}
              updateCardLike={updateCardsLike}
              chagedState={mockWithPhoto}
            />
          }
        />
        <Route
          path="auth"
          element={<Autorization token={token} setToken={setToken} />}
        />
        <Route
          path="registration"
          element={<Autorization token={token} setToken={setToken} />}
        />
        <Route
          path="cupsWidthPhotos"
          element={
            <PageTemplate
              data={mockWithPhoto} //сам масив
              onLikeClick={onLikeClick} //функція лайку
              changedState={setMockWithPhoto} //зміна стейту масива з якого показують данні
              setOpenedCart={setOpenedCart} //зміна відкритої карточки
              updateCardAdd={updateCardAdd} //непотрібна поки
              isShowCarts={true}
              isShowLables={false}
              isShowText={false}
              updateCardLike={updateCardsLike}
            />
          }
        />
        <Route
          path="chameleon"
          element={
            <PageTemplate
              onLikeClick={onLikeClick} //функція лайку
              changedState={setMockChameleon} //зміна стейту масива з якого показують данні
              updateCardAdd={updateCardAdd}
              setOpenedCart={setOpenedCart}
              updateCardLike={updateCardsLike}
              data={mockChameleon}
              isShowCarts={true}
              isShowLables={false}
              isShowText={false}
            />
          }
        />
        <Route
          path="BigSize"
          element={
            <PageTemplate
              onLikeClick={onLikeClick} //функція лайку
              changedState={setMockBigSize} //зміна стейту масива з якого показують данні
              updateCardAdd={updateCardAdd}
              setOpenedCart={setOpenedCart}
              updateCardLike={updateCardsLike}
              data={mockBigSize}
              isShowCarts={true}
              isShowLables={false}
              isShowText={false}
            />
          }
        />
        <Route
          path="Boxs"
          element={
            <PageTemplate
              onLikeClick={onLikeClick} //функція лайку
              changedState={setMockBoxs} //зміна стейту масива з якого показують данні
              data={mockBoxs}
              isShowCarts={true}
              isShowLables={true}
              setOpenedCart={setOpenedCart}
              isShowText={false}
              updateCardAdd={updateCardAdd}
              updateCardLike={updateCardsLike}
            />
          }
        />
        <Route
          path="Delivery"
          element={
            <PageTemplate
              updateCardAdd={updateCardAdd}
              updateCardLike={updateCardsLike}
              data={mockMostPopular}
              isShowCarts={false}
              isShowLables={false}
              isShowText={true}
              title="Про доставку"
              description="Намагаємось доставити ваше замовлення так швидко, як котик біжить як чує звук відкриття холодильнику.
              Оформлені до 18:00 замовлення відправляємо в той же день!
              Доставка Новою Поштою 1-3 дні. Оплатити можна при отриманні на пошті, або по передплаті на карту."
            />
          }
        />
        <Route
          path="AboutUs"
          element={
            <PageTemplate
              updateCardAdd={updateCardAdd}
              updateCardLike={updateCardsLike}
              data={mockMostPopular}
              isShowCarts={false}
              isShowLables={false}
              isShowText={true}
              title="Про нас"
              description="Колись я оновлю цей блок і тут буде інформація про команду і те що ми зробили за весь час."
            />
          }
        />
      </Routes>

      <Feedback />
      <Search />
      <Footer />
    </div>
  );
};

export default App;
