import { Link } from "react-router-dom";
import React from "react";
import "./footer.scss";
const Footer = () => {
  const scrollToTop = () => {
    const c = document.documentElement.scrollTop || document.body.scrollTop;
    if (c > 0) {
      window.requestAnimationFrame(scrollToTop);
      window.scrollTo(0, c - c / 8);
    }
  };
  return (
    <footer className="footer">
      <Link onClick={scrollToTop} to={"/"} className="footer__logo">
        <img src={` ${process.env.PUBLIC_URL}/img/logo.svg`} />
      </Link>
      <ul className="footer__menu">
        <Link onClick={scrollToTop} to={"/chameleon"} className="footer__link">
          Чашки
        </Link>
        <Link onClick={scrollToTop} to={"/BigSize"} className="footer__link">
          Big Size
        </Link>
        <Link onClick={scrollToTop} to={"/custom"} className="footer__link">
          Кастомні чашки
        </Link>
        <Link onClick={scrollToTop} to={"/Boxs"} className="footer__link">
          Бокси
        </Link>
        <Link onClick={scrollToTop} to={"/AboutUs"} className="footer__link">
          Про нас
        </Link>
        <Link onClick={scrollToTop} to={"/Delivery"} className="footer__link">
          Доставка
        </Link>
        <Link
          onClick={scrollToTop}
          to={"/privacy-policy"}
          className="footer__link"
        >
          Політика конфіденційності
        </Link>
        <Link onClick={scrollToTop} to={"/oferta"} className="footer__link">
          Договір публічної оферти
        </Link>
      </ul>
      <ul className="footer__social">
        <li className="footer__social-link">
          <img
            className="footer__social-img"
            src={`${process.env.PUBLIC_URL}/img/instagram-svgrepo-com.svg`}
          />
        </li>
        <li className="footer__social-link">
          <img
            className="footer__social-img"
            src={`${process.env.PUBLIC_URL}/img/telegram-svgrepo-com.svg`}
          />
        </li>
        <li className="footer__social-link">
          <img
            className="footer__social-img"
            src={`${process.env.PUBLIC_URL}/img/viber-chat-app-svgrepo-com.svg`}
          />
        </li>
        <li className="footer__social-link">
          <img
            className="footer__social-img"
            src={`${process.env.PUBLIC_URL}/img/phone-call-svgrepo-com.svg`}
          />
        </li>
      </ul>
    </footer>
  );
};

export default Footer;
