import React, { useEffect, useState } from "react";
import { Link, useAsyncValue } from "react-router-dom";
import Questions from "src/components/questions";
import ImageModal from "src/components/modalImg";
import { Link as ScrollLink } from "react-scroll";

interface InputData {
  type: {
    value: string;
    isValid: boolean;
    errorMessage: string;
  };
  text: {
    value: string;
    isValid: boolean;
    errorMessage: string;
  };
  photos: {
    value: string;
    isValid: boolean;
    errorMessage: string;
  };
  phone: {
    value: string;
    isValid: boolean;
    errorMessage: string;
  };
  comment: {
    value: string;
    isValid: boolean;
    errorMessage: string;
  };
  box: {
    value: string;
    isValid: boolean;
    errorMessage: string;
  };
}

const CustomPage = () => {
  const [basketBox, setBasketBox] = useState([]);
  const [typeOfCups, setTypeOfCups] = useState([]);

  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedImageUrl, setSelectedImageUrl] = useState("");

  const [file, setFile] = useState<any>([]);
  const [inputData, setInputData] = useState<InputData>({
    type: {
      value: "",
      isValid: true,
      errorMessage: "",
    },
    text: {
      value: "",
      isValid: true,
      errorMessage: "",
    },
    phone: {
      value: "",
      isValid: true,
      errorMessage: "",
    },
    photos: {
      value: "",
      isValid: true,
      errorMessage: "",
    },
    comment: {
      value: "",
      isValid: true,
      errorMessage: "",
    },
    box: {
      value: "",
      isValid: true,
      errorMessage: "",
    },
  });

  useEffect(() => {
    fetch(`${process.env.PUBLIC_URL}/data/basket.json`)
      .then((response) => response.json())
      .then((data) => setBasketBox(data))
      .catch(() => console.log("basket not find"));

    fetch(`${process.env.PUBLIC_URL}/data/typeOfCups.json`)
      .then((response) => response.json())
      .then((data) => setTypeOfCups(data))
      .catch(() => console.log("type not find"));
  }, []);

  const handleFileChange = (event: any) => {
    console.log(event.target.files);
    setFile(event.target.files);
  };
  const handleImageClick = (imageUrl: string) => {
    setSelectedImageUrl(imageUrl);
    setIsModalOpen(true);
  };
  const closeModal = () => {
    setIsModalOpen(false);
  };

  const onChangeInput = (event: any) => {
    let { name, value, files } = event.target;
    let isValid = true;
    let errorMessage = "";
    switch (name) {
      case "text":
        value.length > 0 ? (isValid = true) : (isValid = false);
        errorMessage = isValid ? "" : "Ведіть текст";
        break;
      case "phone":
        value = handleInputChange(value);
        value.length === 13 ? (isValid = true) : (isValid = false);
        errorMessage = isValid ? "" : "Ведіть коректний номер телефону";
        break;
      case "box":
        event.target.checked
          ? (event.target.checked = true)
          : (event.target.checked = false);
        break;
      case "file":
        const fileNames = Array.from(Object.values(files)).map(
          (file: any) => file.name
        );
        value = fileNames.join(", ");
        break;
      default:
        break;
    }
    setInputData((prevData: any) => ({
      ...prevData,
      [name]: {
        value,
        isValid,
        errorMessage,
      },
    }));
  };

  const handleInputChange = (value: string) => {
    let formattedInput = value.trim();
    if (formattedInput.startsWith("0")) {
      formattedInput = `+38${formattedInput}`;
    } else if (formattedInput.startsWith("380")) {
      formattedInput = `+${formattedInput}`;
    } else if (formattedInput.startsWith("+")) {
    } else {
    }
    return formattedInput;
  };

  const onSubmitForm = async (event: any) => {
    event.preventDefault();
    let areAllInputsValid = false;

    const checkData = async () => {
      !(inputData.type.value.length > 0)
        ? setInputData((prevData: any) => ({
            ...prevData,
            type: { ...prevData.type, isValid: false },
          }))
        : setInputData((prevData: any) => ({
            ...prevData,
            type: { ...prevData.type, isValid: true },
          }));
      !(inputData.text.value.length > 0)
        ? setInputData((prevData: any) => ({
            ...prevData,
            text: { ...prevData.text, isValid: false },
          }))
        : setInputData((prevData: any) => ({
            ...prevData,
            text: { ...prevData.text, isValid: true },
          }));

      !(inputData.photos.value.length > 0)
        ? setInputData((prevData: any) => ({
            ...prevData,
            photos: { ...prevData.photos, isValid: false },
          }))
        : setInputData((prevData: any) => ({
            ...prevData,
            photos: { ...prevData.photos, isValid: true },
          }));

      !(inputData.phone.value.length == 13)
        ? setInputData((prevData: any) => ({
            ...prevData,
            phone: { ...prevData.phone, isValid: false },
          }))
        : setInputData((prevData: any) => ({
            ...prevData,
            phone: { ...prevData.phone, isValid: true },
          }));

      const check = Object.values(inputData).some(
        (field: any) => field.value.length > 0
      );
      if (check) {
        areAllInputsValid = await Object.values(inputData).every(
          (field: any) => field.isValid
        );
      }
      console.log(check);
    };
    const sendDataToTelegram = async () => {
      console.log("sending");
      const TOKEN = "6619280299:AAGIL6f6uD5nOU1Sjw26zzqvyI0V_fZZKq0";
      const CHAT_ID = "-1002007095666";
      const URL_API = `https://api.telegram.org/bot${TOKEN}`;

      const packing = () => {
        let message = `<b>Унікальна чашка😁</b>

Тип чашки:
${inputData.type.value}
Текст: 
${inputData.text.value}
Номер телефону: 
${inputData.text.value}
Коментар:
${inputData.comment.value.length > 0 ? inputData.comment.value : "Нєма 😔"}
Додатковий бокс:
${inputData.box.value.length > 0 ? inputData.box.value : "Нєма 😔"}
        `;
        return message;
      };

      const sendingImages = () => {
        const formData = new URLSearchParams();
        formData.append("chat_id", CHAT_ID);
        for (const iterator in file) {
          formData.append("photo", iterator);
        }
        formData.append(
          "photo",
          "https://buffer.com/cdn-cgi/image/w=1000,fit=contain,q=90,f=auto/library/content/images/size/w1200/2023/10/free-images.jpg"
        );

        fetch(`${URL_API}/sendPhoto`, {
          method: "POST",
          headers: {
            "Content-Type": "multipart/form-data",
          },
          body: formData,
        })
          .then((response) => {
            console.log(response);
            if (response.ok) {
              console.log("Photo sent successfully");
            } else {
              console.error("Error sending photo");
            }
          })
          .catch((error) => {
            console.error("Error:", error);
          });
      };
      const sendingText = () => {
        const formData = new URLSearchParams();
        formData.append("chat_id", CHAT_ID);
        formData.append("text", packing());
        formData.append("parse_mode", "html");

        fetch(`${URL_API}/sendMessage`, {
          method: "POST",
          body: formData,
        })
          .then((response) => {
            return response;
          })
          .then((response) => response.json())
          .then((data) => {
            console.log("Message sent:", data);
          })
          .catch((error) => {
            console.error("Error:", error);
          });
      };
      try {
        await sendingImages();
      } catch {
        console.log("sd");
      }
      await sendingText();
    };

    // const sendMessageToBackEnd = (orderedCarts: any) => {
    //   const getCurrentDate = new Date().toISOString().split("T")[0];

    //   let filterCarts: any = [];
    //   filterCarts = orderedCarts.map((element: any, index: any) => {
    //     const activeType =
    //       element.type.length > 0
    //         ? element.type.filter((type: any) => type.isActive)
    //         : [];

    //     return {
    //       price: element.price, // Замініть на значення з форми або іншого джерела
    //       quantity: element.count,
    //       name: element.name, // Замініть на значення з форми або іншого джерела
    //       picture: element.images[0], // Замініть на значення з форми або іншого джерела
    //       properties:
    //         activeType.length > 0
    //           ? [
    //               {
    //                 name: "Тип чашки",
    //                 value: activeType[0].name,
    //               },
    //             ]
    //           : null,
    //     };
    //   });
    //   try {
    //     const activeBoxBasket = () => {
    //       return basketBox.filter((element: array) => {
    //         return element.price === boxsPriceBasket;
    //       });
    //     };
    //     const actBox: array = activeBoxBasket()[0];

    //     filterCarts.push({
    //       price: actBox.price, // Замініть на значення з форми або іншого джерела
    //       quantity: 1,
    //       name: actBox.name, // Замініть на значення з форми або іншого джерела
    //       picture: actBox.img, // Замініть на значення з форми або іншого джерела
    //       properties: [],
    //     });
    //   } catch {
    //     console.log("basket is not choose");
    //   }

    //   const dataForBack = {
    //     source_id: 2,
    //     client_comment: inputData.comment,
    //     buyer: {
    //       full_name: `${inputData.fullName.value}`, // Замініть на значення з форми або іншого джерела
    //       // email: "john.doe@example.com", // Замініть на значення з форми або іншого джерела
    //       phone: `${inputData.phoneNumber.value}`, // Замініть на значення з форми або іншого джерела
    //     },
    //     shipping: {
    //       shipping_address_city: `${inputData.phoneNumber.value}`, // Замініть на значення з форми або іншого джерела
    //       shipping_address_region: `${inputData.novaPoshtaBranch.value}`, // Замініть на значення з форми або іншого джерела
    //     },
    //     products: filterCarts,
    //   };

    //   fetch("https://1f10-88-155-140-169.ngrok-free.app/send-to-keycrm", {
    //     method: "POST",
    //     mode: "cors",
    //     headers: {
    //       "Content-Type": "application/json",
    //       Accept: "application/json",
    //       "Cache-Control": "no-cache",
    //       Pragma: "no-cache",
    //     },
    //     body: JSON.stringify(dataForBack),
    //   })
    //     .then((response) => {
    //       if (!response.ok) {
    //         throw new Error(`HTTP error! Status: ${response.status}`);
    //       }
    //       return response.json();
    //     })
    //     .then((data) => console.log(data))
    //     .catch((error) => console.error("Error:", error));
    // };

    await checkData();
    if (areAllInputsValid) {
      sendDataToTelegram();
    }
  };

  return (
    <>
      <ImageModal
        isOpen={isModalOpen}
        closeModal={closeModal}
        imageUrl={selectedImageUrl}
      />
      <section
        className="customFirstSection"
        style={{
          backgroundImage: `url(${process.env.PUBLIC_URL}/img/customBg.png)`,
        }}
      >
        <div className="customFirstSection_wrapper">
          <div className="container">
            <h2 className="customFirstSection_title title">
              Створи свою неповторну чашку!
            </h2>
            <p className="customFirstSection_description">
              Хочеш подарувати незабутні емоції для своїх рідних і близьких?
              Кастомізуй чашку, додай свій текст та картинки, які хочеш нанести
              на чашку. Кращого подарунку просто не існує!
            </p>
            <ScrollLink
              to="yourElement"
              spy={true}
              smooth={true}
              duration={500}
            >
              <button className="customFirstSection_button firstSection__button">
                зробити свою чашку
              </button>
            </ScrollLink>
          </div>
        </div>
      </section>
      <section className="secondSection">
        <div className="container">
          <h2 className="secondSection_title title">
            Як замовити кастомну чашку зі своїм дизайном?
          </h2>
          <div className="secondSection_row">
            <article className="secondSection_block">
              <h3 className="secondSection_block_title">Завантажити фото</h3>
              <p className="secondSection_block_description">
                Завантажте фото або картинку, яку хочете нанести на чашку.
              </p>
