import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

interface PaymentRequest {
  amount: number;
  description: string;
  accountId: string; // Your Monobank account ID
  apiKey: string; // Your Monobank API key
}

const PaymentComponent: React.FC = () => {
  const [text, setText] = useState("");
  const history = useNavigate();

  const getDataFromMonoUser = () => {
    fetch("https://api.monobank.ua/api/merchant/invoice/create", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "X-Token": "utR_bnF6LUzdc4pr3yFNFF2kKEPk75xeIlItZx9QfaxY",
      },
      body: JSON.stringify({
        amount: 1000,
        redirectUrl: "https://mister-gifter.com/",
        merchantPaymInfo: {
          reference: "84d0070ee4e44667b31371d8f8813947",
          destination: "Подарунок від MISTER GIFTER",
          comment: "Подарунок від MISTER GIFTER",
          customerEmails: [
            "ivan.kalunuch12@gmail.com",
            "ivan.kalunuch31@gmail.com",
          ],
          basketOrder: [
            {
              name: "Табуретка",
              qty: 2,
              sum: 2100,
              icon: "https://thumb.tildacdn.com/stor3635-6363-4864-a664-353761303838/-/format/webp/91148690.png",
              unit: "шт.",
              code: "111",
              barcode: "string",
              header: "string",
              footer: "string",
              tax: [],
              uktzed: "string",
              discounts: [
                {
                  type: "DISCOUNT",
                  mode: "VALUE",
                  value: "1000",
                },
              ],
            },
          ],
        },
      }),
    })
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        console.log(data);
        window.location.href = data.pageUrl;
      })

      //   "basketOrder":[
      //     {
      //       "name":"xxxxxxxxxxxx",
      //       "qty":1,
      //       "sum":29900,
      //       "icon":"https://xxxxxxxx.xxxxx/img/1a16c182-09b4-4ae9-8063-bc5e4a4c23d3.png",
      //       "unit":"xxx."
      //     }
      //   ]

      .catch((response) => console.log(response));
  };

  return (
    <div style={{ textAlign: "center", marginTop: "100px" }}>
      <h1>Привіт</h1>
      <p> тут тестую monopay</p>
      <p> </p>
      <button onClick={getDataFromMonoUser}>Get Data</button>
    </div>
  );
};

export default PaymentComponent;
