import React, { ReactNode, useEffect, useState } from "react";
import { Link, useLocation } from "react-router-dom";
import Cart from "src/components/cart";
import QuestionLabel from "src/components/question";

interface PropsType {
  data: Array<any>;
  isShowLables: boolean;
  isShowText: boolean;
  isShowCarts: boolean;
  title?: string;
  description?: string;
  updateCardLike: any;
  updateCardAdd: any;
  setOpenedCart?: any;
}
const PageTemplate: React.FC<PropsType> = ({
  data,
  isShowLables,
  isShowText,
  isShowCarts,
  setOpenedCart,
  title,
  description,
  updateCardLike,
  updateCardAdd,
}) => {
  const [labelDescription, seDescription] = useState("");

  useEffect(() => {
    window.scrollTo(0, 0);
    seDescription(`s;ladkfjal;ksdfj \n a;sdlkjfalskdjfalksdjfal;kdsj`);
  }, []);

  const showCarts = (): ReactNode[] => {
    return data.map((cart, index) => (
      <Link
        to={`/cart/${cart.id}`}
        key={cart.id}
        onClick={() => setOpenedCart(cart)}
      >
        <Cart
          updateCardAdd={updateCardAdd}
          cart={cart}
          updateCardLike={updateCardLike}
        />
      </Link>
    ));
  };

  return (
    <>
      <div className="page__wrapper">{isShowCarts ? showCarts() : null}</div>
      <div className="page__container">
        {isShowLables ? (
          <>
            <QuestionLabel
              title="Детальніше про продукт"
              description={labelDescription}
            />
            <QuestionLabel
              title="Доставка і оплата"
              description={`Оформлені до 18:00 замовлення відправляємо в той же день!<br/>
        Доставка Новою Поштою 1-3 дні`}
            />
          </>
        ) : null}
        {isShowText ? (
          <>
            <h2 className="page__title">{title}</h2>
            <p className="page__description">{description}</p>
          </>
        ) : null}
      </div>
    </>
  );
};
export default PageTemplate;
