import React, { useState, useEffect } from "react";
import QuestionLabel from "src/components/question";
import Slider from "src/components/slider";
import { Swiper, SwiperSlide } from "swiper/react";
import { useParams } from "react-router-dom";
import axios from "axios";
import {
  Navigation,
  FreeMode,
  Pagination,
  Scrollbar,
  EffectFade,
} from "swiper/modules";

import "swiper/css/navigation";
import "swiper/css/pagination";
import "swiper/css/scrollbar";
interface MyComponentProps {
  cards: any;
  setData: any;
  like: any;
  setLikeMock: any;
  addMock: any;
  setAddMock: any;
  updateCard: any;
}

const CartPage: React.FC<MyComponentProps> = ({
  cards,
  setData,
  like,
  setLikeMock,
  addMock,
  setAddMock,
  updateCard,
}) => {
  window.scrollTo(0, 0);

  const { id } = useParams();
  const [card, setCard] = useState({
    id: "",
    name: "",
    price: 0,
    oldPrice: 0,
    description: "",
    about: "",
    imgOrdered: [],
    images: [],
    type: [],
    isLike: false,
    isAdd: false,
  });

  useEffect(() => {
    axios
      .get(`https://65646353ceac41c0761e1278.mockapi.io/data/${id}`)
      .then((response) => setCard(response.data))
      .catch((error) => console.error("Error fetching card details:", error));
  }, [id]);

  const handleLikeClick = (element: React.MouseEvent<HTMLButtonElement>) => {
    element.preventDefault();
    // Оновити карточку та викликати функцію оновлення в CardList

    const updatedCard = { ...card, isLike: !card.isLike };
    setCard(updatedCard);
    updateCard(updatedCard);

    // Відправлення запиту на сервер для збереження змін в базі даних

    axios
      .put(`https://65646353ceac41c0761e1278.mockapi.io/data/${id}`, {
        isLike: !card.isLike,
      })
      .then((response) => console.log("Like status updated successfully"))
      .catch((error) => console.error("Error updating like status:", error));
  };

  const ShowOrderedCups = () => {
    if (card.imgOrdered === undefined) {
      return null;
    }
    return card.imgOrdered.map((imgUrl: string, index: number) => {
      return (
        <img
          className="CartPage__orderedImg"
          src={`${process.env.PUBLIC_URL}/img/carts/${imgUrl}`}
          key={index}
        />
      );
    });
  };

  const ShowCheckBoxs = () => {
    const ShowActiveBoxs = () => {
      return card.type.map((element: any, index: number) => {
        if (element.isActive) {
          return (
            <>
              <h3 className="CartPage__checkBoxs_title">Чашка:</h3>
              <div className="CartPage__checkBoxs_active" key={index}>
                <img
                  className="CartPage__checkBoxs_img"
                  src={`${process.env.PUBLIC_URL}/img/carts/${element.img}`}
                />
                <h3 className="CartPage__checkBoxs_title">{element.name}</h3>
              </div>
            </>
          );
        }
      });
    };
    const ShowOther = () => {
      return card.type.map((element: any, index: number) => {
        return (
          <div
            className={
              element.isActive
                ? "CartPage__checkBoxs_row CartPage__checkBoxs_row--active "
                : "CartPage__checkBoxs_row "
            }
            key={index}
          >
            <img
              className="CartPage__checkBoxs_img"
              src={`${process.env.PUBLIC_URL}/img/carts/${element.img}`}
            />
            <h3 className="CartPage__checkBoxs_title">{element.name}</h3>
          </div>
        );
      });
    };
    return (
      <div className="CartPage__checkBoxs_block">
        <ShowActiveBoxs />
        <div className="CartPage__checkBoxs_wrapper ">
          <ShowOther />
        </div>
      </div>
    );
  };
  const pagination = {
    clickable: true,
    renderBullet: function (index: any, className: any) {
      console.log(index);
      return (
        '<span class="' +
        className +
        '">' +
        `<img src ="${process.env.PUBLIC_URL}/img/carts/${card.images[index]}"/>` +
        "</span>"
      );
    },
  };

  return (
    <div className="CartPage">
      <Swiper
        modules={[Navigation, FreeMode, Pagination, Scrollbar, EffectFade]}
        effect={"fade"}
        className="CartPage__slider"
        slidesPerView={1}
        navigation={true}
        spaceBetween={30}
        loop={true}
        pagination={pagination}
      >
        {card.images.map((img: string, index: number) => {
          return (
            <SwiperSlide className="CartPage__slide" key={index}>
              <img
                className="CartPage__img"
                src={`${process.env.PUBLIC_URL}/img/carts/${img}`}
              />
            </SwiperSlide>
          );
        })}
      </Swiper>
      <h2 className="CartPage__title">{card.name}</h2>
      <div className="CartPage__row">
        <span className="CartPage__price">{card.price} ₴</span>
        <span className="CartPage__oldPrice">{card.oldPrice} ₴</span>
      </div>
      <ShowCheckBoxs />
      <div className="CartPage__row">
        <button className="CartPage__button">Замовити</button>
        <button
          className={
            card.isLike
              ? "CartPage__like CartPage__like_active"
              : "CartPage__like"
          }
          onClick={handleLikeClick}
        >
          <svg
            width="26"
            height="23"
            viewBox="0 0 26 23"
            fill="none"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path
              d="M24.3095 7.82226C24.3095 14.1423 13 20.8677 13 20.8677C13 20.8677 1.69043 14.1423 1.69043 7.82226C1.69043 -0.758756 13 -0.642878 13 6.90926C13 -0.642878 24.3095 -0.529907 24.3095 7.82226Z"
              stroke="black"
              stroke-width="1.5"
              stroke-linejoin="round"
            />
          </svg>
        </button>
      </div>
      <p
        className="CartPage__text"
        dangerouslySetInnerHTML={{ __html: card.description }}
      ></p>
      <div className="CartPage__block">
        <h2 className="CartPage__block_title">
          Чашки, які вже замовили з цим дизайном
        </h2>
        <div className="CartPage__block_wrapper">
          <ShowOrderedCups />
        </div>
      </div>
      <QuestionLabel title="Детальніше про продукт" description={card.about} />
      <QuestionLabel
        title="Доставка і оплата"
        description={`Оформлені до 18:00 замовлення відправляємо в той же день!\n
Доставка Новою Поштою 1-3 дні\n
Оплатити замовлення можна при отриманні на Новій пошті (*крім кастомних чашок), або по передплаті на банківський рахунок.`}
      />
      <Slider data={cards} />
    </div>
  );
};
export default CartPage;
