import axios from "axios";
import React, { useState, useEffect } from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import { useParams } from "react-router-dom";

import Slider from "src/components/slider";
import QuestionLabel from "src/components/question";
import ImageModal from "src/components/modalImg";

import {
  Navigation,
  FreeMode,
  Pagination,
  Scrollbar,
  EffectFade,
} from "swiper/modules";

import "swiper/css/navigation";
import "swiper/css/pagination";
import "swiper/css/scrollbar";

interface MyComponentProps {
  cards: any;
  setData: any;
  like: any;
  setLikeMock: any;
  addMock: any;
  setAddMock: any;
  updateCardLike: any;
  updateCardAdd: any;
  setOpenedCart: any;
  openedCart: any;
}

const CartPage: React.FC<MyComponentProps> = ({
  cards,
  setData,
  like,
  openedCart,
  setLikeMock,
  setOpenedCart,
  addMock,
  setAddMock,
  updateCardLike,
  updateCardAdd,
}) => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedImageUrl, setSelectedImageUrl] = useState("");
  const [isTypeOpen, setTypeOpen] = useState(false);
  const { id } = useParams();
  const [cart, setCard] = useState({
    id: "",
    name: "",
    price: 0,
    oldPrice: 0,
    description: "",
    about: "",
    imgOrdered: [],
    images: [],
    type: [],
    isLike: false,
    isAdd: false,
  });

  useEffect(() => {
    window.scrollTo(0, 0);
  }, [id]);

  const handleImageClick = (imageUrl: string) => {
    setSelectedImageUrl(imageUrl);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const handleLikeClick = (element: React.MouseEvent<HTMLButtonElement>) => {
    element.preventDefault();
    const updatedCard = { ...openedCart, isLike: !openedCart.isLike };
    setCard(updatedCard);
    updateCardLike(updatedCard);

    axios
      .put(`https://65646353ceac41c0761e1278.mockapi.io/data/${id}`, {
        isLike: !openedCart.isLike,
      })
      .then((response) => console.log("Like status updated successfully"))
      .catch((error) => console.error("Error updating like status:", error));
  };

  const handleAddClick = (event: React.MouseEvent<HTMLButtonElement>) => {
    event.preventDefault();
    const updatedCart = { ...openedCart, isAdd: !openedCart.isAdd };
    setCard(updatedCart);
    updateCardAdd(updatedCart);
    axios
      .put(
        `https://65646353ceac41c0761e1278.mockapi.io/data/${updatedCart.id}`,
        {
          isAdd: !openedCart.isAdd,
        }
      )
      .then((response) => console.log("add status updated successfully"))
      .catch((error) => console.error("Error updating like status:", error));
  };

  const ShowOrderedCups = () => {
    if (openedCart.imgOrdered === undefined) {
      return null;
    }
    return openedCart.imgOrdered.map((imgUrl: string, index: number) => {
      return (
        <img
          onClick={() =>
            handleImageClick(`${process.env.PUBLIC_URL}/img/${imgUrl}`)
          }
          className="CartPage__orderedImg"
          src={`${process.env.PUBLIC_URL}/img/${imgUrl}`}
          key={index}
        />
      );
    });
  };

  const ShowCheckBoxs = () => {
    const ShowActiveBoxs = () => {
      return openedCart.type.map((element: any, index: number) => {
        if (element.isActive) {
          return (
            <div key={index}>
              <h3 className="CartPage__checkBoxs_title">Чашка:</h3>
              <div className="CartPage__checkBoxs_active">
                <img
                  className="CartPage__checkBoxs_img"
                  src={`${process.env.PUBLIC_URL}/img/${element.img}`}
                />
                <h3 className="CartPage__checkBoxs_title">{element.name}</h3>
              </div>
            </div>
          );
        }
      });
    };
    const ShowOther = () => {
      return openedCart.type.map((element: any, index: number) => {
        return (
          <div
            className={
              element.isActive
                ? "CartPage__checkBoxs_row CartPage__checkBoxs_row--active "
                : "CartPage__checkBoxs_row "
            }
            key={index}
          >
            <img
              className="CartPage__checkBoxs_img"
              src={`${process.env.PUBLIC_URL}/img/${element.img}`}
            />
            <h3 className="CartPage__checkBoxs_title">{element.name}</h3>
          </div>
        );
      });
    };
    return (
      <div className="CartPage__checkBoxs_block">
        <ShowActiveBoxs />
        <div
          className={
            isTypeOpen
              ? "CartPage__checkBoxs_wrapper CartPage__checkBoxs_wrapper--active"
              : "CartPage__checkBoxs_wrapper"
          }
        >
          <ShowOther />
        </div>
      </div>
    );
  };
  const pagination = {
    clickable: true,
    renderBullet: function (index: any, className: any) {
      return (
        '<span class="' +
        className +
        '">' +
        `<img src ="${process.env.PUBLIC_URL}/img/${openedCart.images[index]}"/>` +
        "</span>"
      );
    },
  };

  return (
    <div className="CartPage">
      <ImageModal
        isOpen={isModalOpen}
        closeModal={closeModal}
        imageUrl={selectedImageUrl}
      />
      <Swiper
        modules={[Navigation, FreeMode, Pagination, Scrollbar, EffectFade]}
        effect={"fade"}
        className="CartPage__slider"
        slidesPerView={1}
        navigation={true}
        spaceBetween={30}
        loop={true}
        pagination={pagination}
      >
        {openedCart.images.map((img: string, index: number) => {
          return (
            <SwiperSlide className="CartPage__slide" key={index}>
              <img
                onClick={(event) => {
                  console.log(img);
                  handleImageClick(`${process.env.PUBLIC_URL}/img/${img}`);
                }}
                className="CartPage__img"
                src={`${process.env.PUBLIC_URL}/img/${img}`}
              />
            </SwiperSlide>
          );
        })}
      </Swiper>
      <h2 className="CartPage__title">{openedCart.name}</h2>
      <div className="CartPage__row">
        <span className="CartPage__price">{openedCart.price} ₴</span>
        <span className="CartPage__oldPrice">{openedCart.oldPrice} ₴</span>
      </div>
      <ShowCheckBoxs />
      <div className="CartPage__row">
        <button className="CartPage__button" onClick={handleAddClick}>
          Замовити
        </button>
        <button
          className={
            openedCart.isLike
              ? "CartPage__like CartPage__like_active"
              : "CartPage__like"
          }
          onClick={handleLikeClick}
        >
          <svg
            width="26"
            height="23"
            viewBox="0 0 26 23"
            fill="none"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path
              d="M24.3095 7.82226C24.3095 14.1423 13 20.8677 13 20.8677C13 20.8677 1.69043 14.1423 1.69043 7.82226C1.69043 -0.758756 13 -0.642878 13 6.90926C13 -0.642878 24.3095 -0.529907 24.3095 7.82226Z"
              stroke="black"
              stroke-width="1.5"
              stroke-linejoin="round"
            />
          </svg>
        </button>
      </div>
      <p
        className="CartPage__text"
        dangerouslySetInnerHTML={{ __html: openedCart.description }}
      ></p>
      {openedCart.imgOrdered.length > 0 ? (
        <div className="CartPage__block">
          <h2 className="CartPage__block_title">
            Чашки, які вже замовили з цим дизайном
          </h2>
          <div className="CartPage__block_wrapper">
            <ShowOrderedCups />
          </div>
        </div>
      ) : null}
      <QuestionLabel
        title="Детальніше про продукт"
        description={openedCart.about}
      />
      <QuestionLabel
        title="Доставка і оплата"
        description={`Оформлені до 18:00 замовлення відправляємо в той же день!\n
Доставка Новою Поштою 1-3 дні\n
Оплатити замовлення можна при отриманні на Новій пошті (*крім кастомних чашок), або по передплаті на банківський рахунок.`}
      />
      <Slider
        setOpenedCart={setOpenedCart}
        updateCardLike={updateCardLike}
        updateCardAdd={updateCardAdd}
        data={cards}
      />
    </div>
  );
};
export default CartPage;
