import React from "react";

interface ISendCRMProps {
  //   data: any; // Тип даних може бути змінений залежно від структури даних, яку ви відправляєте на сервер KeyCRM
}

const SendCRM: React.FC<ISendCRMProps> = ({}) => {
  const token = "ZTg5MWY5NDE4YWRkYTBjZDIzODQ0Mjc4NzRmYmM2MWQxMGY0MmQzZg";
  const data = {
    source_id: 1,
    buyer: {
      full_name: "ivan",
      email: "ivan.kalunuch12@gmail.com",
      phone: "+380934638895",
    },
    shipping: {
      shipping_address_city: "Scole",
      shipping_receive_point: "Ivan Franko 42a",
      shipping_address_country: "Ukraine",
      shipping_address_region: "Lvivska Oblast",
      shipping_address_zip: "23333",
    },
    products: [
      {
        price: 1,
        quantity: 1,
        name: "Чашка",
        picture:
          "https://thumb.tildacdn.com/stor3635-6363-4864-a664-353761303838/-/format/webp/91148690.png",
        properties: [
          {
            name: "Color",
            value: "хамелеон",
          },
        ],
      },
    ],
  };

  const sendRequest = async () => {
    const url = "https://openapi.keycrm.app/v1/order"; // URL для відправлення даних на KeyCRM

    try {
      const response = await fetch(url, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Accept: "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(data),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const responseData = await response.json();

      // Викликаємо колбек onSuccess з отриманими даними
      //   onSuccess && onSuccess(responseData);
    } catch (error: any) {
      // Викликаємо колбек onError з об'єктом помилки
      //   onError && onError(error);
    }
  };

  return (
    <div>
      <button onClick={sendRequest}>Відправити на KeyCRM</button>
    </div>
  );
};

export default SendCRM;
