import React, { useState } from "react";

interface PaymentRequest {
  amount: number;
  description: string;
  accountId: string; // Your Monobank account ID
  apiKey: string; // Your Monobank API key
}

const PaymentComponent: React.FC = () => {
  const [paymentRequest, setPaymentRequest] = useState<PaymentRequest>({
    amount: 100,
    description: "Service payment",
    accountId: "YOUR_ACCOUNT_ID",
    apiKey: "utR_bnF6LUzdc4pr3yFNFF2kKEPk75xeIlItZx9QfaxY",
  });

  const handlePayment = async () => {
    try {
      const response = await initiatePayment(paymentRequest);
      console.log("Payment initiated successfully:", response);
    } catch (error) {
      console.error("Error initiating payment:", error);
    }
  };

  const initiatePayment = async (request: PaymentRequest) => {
    const apiUrl = "https://api.monobank.ua/api/merchant/invoice/create";

    const requestOptions: RequestInit = {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "X-Token": request.apiKey,

        amount: 4200,
        ccy: 980,
        merchantPaymInfo: {
          reference: "84d0070ee4e44667b31371d8f8813947",
          destination: "Покупка щастя",
          comment: "Покупка щастя",
          customerEmails: [],
          basketOrder: [],
        },
        redirectUrl: "https://example.com/your/website/result/page",
        webHookUrl:
          "https://example.com/mono/acquiring/webhook/maybesomegibberishuniquestringbutnotnecessarily",
        validity: 3600,
        paymentType: "debit",
        qrId: "XJ_DiM4rTd5V",
        code: "0a8637b3bccb42aa93fdeb791b8b58e9",
        saveCardData: {
          saveCard: true,
          walletId: "69f780d841a0434aa535b08821f4822c",
        },
      },
      body: JSON.stringify({
        account: request.accountId,
        amount: request.amount * 100,
        description: request.description,
      }),
    };

    const response = await fetch(apiUrl, requestOptions);

    if (!response.ok) {
      throw new Error(`Payment initiation failed: ${response.statusText}`);
    }

    return response.json();
  };

  return (
    <div style={{ marginTop: "100px" }}>
      <h1>Payment Page</h1>
      <p>Amount: {paymentRequest.amount} UAH</p>
      <button onClick={handlePayment}>Pay Now</button>
    </div>
  );
};

export default PaymentComponent;
