// store.js

import { makeAutoObservable } from "mobx";


class MyStore {
    data = [];
    jsonFiles = ['bigSize.json', 'boxs.json', 'chameleons.json', "cupsWithPhotos.json"];

    constructor() {
        makeAutoObservable(this)
    }

    async loadData() {
        try {
            const promises = jsonFiles.map(async (fileName) => {
                const response = await fetch(`/src/${fileName}`);
                const data = await response.json();
                return data;
            });

            const allData = await Promise.all(promises);

            // Объедините данные из всех файлов в один массив
            this.data = allData.flat();

        } catch (error) {
            console.error('Error loading data:', error);
        }
    }
}

const myStore = new MyStore();
export default myStore;
