import Slider from "src/components/slider";
import Questions from "src/components/questions";
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import NotLoadedCarts from "src/components/notLoadedCarts ";

interface Prop {
  popular: any;
  bigSize: any;
  boxs: any;
  onLike?: any;
  onAdd?: any;
  loading: boolean;
}

const HomePage: React.FC<Prop> = ({
  loading,
  onAdd,
  onLike,
  popular,
  bigSize,
  boxs,
}) => {
  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);

  return (
    <>
      x
      <section
        className="firstSection"
        style={{ background: `url("img/BG.jpg")` }}
      >
        <h1 className="firstSection__title">Mister Gifter</h1>
        <p className="firstSection__description">Найдивовижніші подарунки!</p>
        <Link to="/chameleon">
          <button className="firstSection__button">дивитись чашки</button>
        </Link>
      </section>
      <Slider
        loading={loading}
        data={popular}
        onAdd={onAdd}
        onLike={onLike}
        title="Найпопулярніші товари"
      />
      <NotLoadedCarts />
      <Slider
        loading={loading}
        data={bigSize}
        onAdd={onAdd}
        onLike={onLike}
        title="Big Size"
      />
      <section className="box">
        <h2 className="title box__title ">Вибери потрібний подарунок</h2>
        <Link to={"/Boxs"}>
          <div className="box__img-wrapper">
            <img className="box__img" src="img/92757869.png" />
          </div>
        </Link>
        <h2 className="title box__title-blue ">Для нього</h2>

        <Link to={"/Boxs"}>
          <div className="box__img-wrapper">
            <img className="box__img" src="img/secondBox.png" />
          </div>
        </Link>
        <h2 className="title box__title-red">Для неї</h2>
      </section>
      <Slider
        loading={loading}
        data={boxs}
        onAdd={onAdd}
        onLike={onLike}
        title={"Подуранкові бокси"}
      />
      <Questions />
    </>
  );
};
export default HomePage;
