interface ITodo {
  completed: boolean;
  task: string;
  assignee: null;
}
class TodoStore {
  todos: Array<ITodo> = [];

  get completedTodosCount() {
    return this.todos.filter((todo: ITodo) => todo.completed === true).length;
  }

  report() {
    if (this.todos.length === 0) {
      return "<none>";
    }
    var nextTodo: ITodo | undefined = this.todos.find(
      (todo: ITodo) => todo.completed === false
    );
    return `Next todo: "${nextTodo ? nextTodo.task : "<none>"}. Progress: ${
      this.completedTodosCount
    }/${this.todos.length}`;
  }
  addTodo(task: string) {
    this.todos.push({
      task: task,
      completed: false,
      assignee: null,
    });
  }
}
export default new TodoStore();
