import { useState } from "react";

const Cart = ({ name, price, oldPrice, redText, images }) => {
  const [isLike, setLike] = useState(false);
  const [isAdd, setAdd] = useState(false);
  const [firstImg, secondImg] = images;
  return (
    <cart className="slider__cart">
      <div className="slider__cart-img-block">
        <img classList="slider__cart-img" src={`img/carts/${firstImg}`} />
        <img classList="slider__cart-img" src={`img/carts/${secondImg}`} />
      </div>
      <h3 className="slider__cart-title">{name}</h3>
      <div className="slider__cart-price">
        <span className="slider__cart-price-current">{price}$</span>
        <span className="slider__cart-price-old">{oldPrice}$</span>
      </div>
      <button
        className="slider__cart-button"
        onClick={() => setAdd((status) => !status)}
      >
        Додати в кошик
      </button>
      <button
        className="slider__cart-like"
        onClick={() => setLike((status) => !status)}
      >
        <img src="img/heart-svgrepo-com.svg" />
      </button>
      <span className="slider__cart-button-red">{redText}</span>
    </cart>
  );
};

export default Cart;
