import Cart from "../cart";
import { Swiper, SwiperSlide } from "swiper/react";
import { Scrollbar, FreeMode } from "swiper/modules";
import { Link } from "react-router-dom";
import "swiper/css";
import "swiper/css/scrollbar";
import React, { ReactNode } from "react";
interface YourComponentProps {
  title?: string;
  data: Array<any>;
  updateCardLike?: any;
  updateCardAdd?: any;
}
const Slider: React.FC<YourComponentProps> = ({
  title,
  data,
  updateCardLike,
  updateCardAdd,
}) => {
  const showCarts = (): ReactNode[] => {
    return data.map((cart, index) => (
      <SwiperSlide key={index}>
        <Link to={`/cart/${cart.id}`}>
          <Cart
            cart={cart}
            updateCardAdd={updateCardAdd}
            updateCardLike={updateCardLike}
          />
        </Link>
      </SwiperSlide>
    ));
  };
  return (
    <section className="popular">
      <h2 className="popular__title title">{title}</h2>
      <div className="popular__slider slider">
        <Swiper
          className="slider__row"
          modules={[FreeMode, Scrollbar]}
          spaceBetween={35}
          freeMode={true}
          scrollbar={{
            hide: true,
          }}
          slidesPerView={1.3}
        >
          {showCarts()}
        </Swiper>
      </div>
    </section>
  );
};
export default Slider;
