import Cart from "../cart";
import { Swiper, SwiperSlide } from "swiper/react";
import { Scrollbar, FreeMode } from "swiper/modules";
import { Link } from "react-router-dom";
import "swiper/css";
import "swiper/css/scrollbar";
import React, { ReactNode } from "react";
interface YourComponentProps {
  title?: string;
  data: Array<any>;
  onLike?: any;
  onAdd?: any;
}
const Slider: React.FC<YourComponentProps> = ({
  onAdd,
  title,
  data,
  onLike,
}) => {
  const showCarts = (): ReactNode[] => {
    return data.map((cart, index) => (
      <SwiperSlide key={index}>
        <Link to={`/cart/${cart.id}`}>
          <Cart carts={data} cart={cart} onAdd={onAdd} onLike={onLike} />
        </Link>
      </SwiperSlide>
    ));
  };
  return (
    <section className="popular">
      <h2 className="popular__title title">{title}</h2>
      <div className="popular__slider slider">
        <Swiper
          className="slider__row"
          modules={[FreeMode, Scrollbar]}
          freeMode={true}
          breakpoints={{
            0: {
              slidesPerView: 1.3,
              spaceBetween: 35,
            },
            580: {
              slidesPerView: 2.5,
              spaceBetween: 35,
            },
            750: {
              slidesPerView: 3.5,
              spaceBetween: 35,
            },
            1024: {
              slidesPerView: 5,
              spaceBetween: 50,
            },
          }}
        >
          {showCarts()}
        </Swiper>
      </div>
    </section>
  );
};
export default Slider;
