import React, { useEffect, useState } from "react";
const CustomPage = () => {
  const [basketBox, setBasketBox] = useState([]);

  useEffect(() => {
    fetch(`${process.env.PUBLIC_URL}/data/basket.json`)
      .then((response) => response.json())
      .then((data) => setBasketBox(data))
      .catch(() => console.log("basket not find"));
  }, []);

  return (
    <>
      <section>
        <div>
          <div>
            {" "}
            <h2>Створи свою неповторну чашку!</h2>
            <p>
              Хочеш подарувати незабутні емоції для своїх рідних і близьких?
              Кастомізуй чашку, додай <br /> свій текст та картинки, які хочеш
              нанести на чашку. Кращого подарунку просто не існує!
            </p>
            <button>зробити свою чашку</button>
          </div>
        </div>
      </section>
      <section>
        <div>
          <h2>Як замовити кастомну чашку зі своїм дизайном?</h2>
          <article>
            <h3>Завантажити фото</h3>
            <p>Завантажте фото або картинку, яку хочете нанести на чашку.</p>
          </article>
        </div>
      </section>
      <form>
        <h2>Створи свою унікальну чашку!</h2>
        <div>
          <h3>Тип чашки:</h3>
          <div>
            {basketBox.map((element: any, index: any) => {
              return (
                <article key={index}>
                  <input
                    key={index}
                    id={`${element.name} ${index}`}
                    className="drawer__box-checkbox"
                    type="radio"
                    name="RADIOBOXS"
                  />
                  <label
                    // onClick={() => setBoxsPriceBasket(element.price)}
                    key={index}
                    htmlFor={`${element.name} ${index}`}
                    className="drawer__box"
                  >
                    <img
                      src={`${process.env.PUBLIC_URL}/img/${element.img}`}
                      className="drawer__box-img"
                    />
                    <h5 className="drawer__box-title">{element.name}</h5>
                  </label>
                </article>
              );
            })}
          </div>
        </div>
      </form>
    </>
  );
};
export default CustomPage;
