import React, { ReactNode } from "react";
import { Link } from "react-router-dom";
import Cart from "src/components/cart";
import QuestionLabel from "src/components/question";
interface PropsType {
  data: Array<any>;
  isShowLables: boolean;
  isShowText: boolean;
  isShowCarts: boolean;
  title?: string;
  description?: string;
}
const PageTemplate: React.FC<PropsType> = ({
  data,
  isShowLables,
  isShowText,
  isShowCarts,
  title,
  description,
}) => {
  window.scrollTo(0, 0);
  const showCarts = (): ReactNode[] => {
    return data.map(({ id, name, price, oldPrice, redText, images }, index) => (
      <Link to={`/cards/${id}`} key={id}>
        <Cart
          name={name}
          price={price}
          oldPrice={oldPrice}
          redText={redText}
          images={images}
          key={index}
        />
      </Link>
    ));
  };
  return (
    <>
      <div className="page__wrapper">{isShowCarts ? showCarts() : null}</div>
      <div className="page__container">
        {isShowLables ? (
          <>
            <QuestionLabel
              title="Детальніше про продукт"
              description={`
Подарунковий бокс, йде тільки в комплекті з Чашкою або Бокалом.\n
Гарантує успішну доставку, оскільки бокс окрім своєї краси, є іще і доволі міцним (В нової пошти немає шансів)\n
Бокс прикрашений декоративним сіном - всередині і подарунковою стрічку з бантиком - ззовні.\n
\n 
і ще багато води, бла бла бла... якщо щось не розписав, напишіть мені в телеграм, відповідаю швидко`}
            />
            <QuestionLabel
              title="Доставка і оплата"
              description={`Доставка і оплата \n
        Оформлені до 18:00 замовлення відправляємо в той же день!\n
        \n
        Доставка Новою Поштою 1-3 дні`}
            />
          </>
        ) : null}
        {isShowText ? (
          <>
            <h2 className="page__title">{title}</h2>
            <p className="page__description">{description}</p>
          </>
        ) : null}
      </div>
    </>
  );
};
export default PageTemplate;
