import React from "react";
interface PropTypes {
  smallButton: boolean;
  Cart?: any;
  updateCard: any;
}
const DrawerCart: React.FC<PropTypes> = ({ smallButton, Cart, updateCard }) => {
  const updatedCart = { ...Cart, isLike: !Cart.isLike };

  const CheckData = () => {
    if (smallButton) {
      return (
        <>
          <p className="drawer__cart-minus">-</p>
          <p className="drawer__cart-counter">1</p>
          <p className="drawer__cart-plus">+</p>
        </>
      );
    } else {
      return null;
    }
  };
  return (
    <div className="drawer__cart">
      <div className="drawer__cart-img-block">
        <img
          src={`${process.env.PUBLIC_URL}/img/carts/${Cart.images[0]}`}
          className="drawer__cart-img"
        />
      </div>
      <div className="drawer__cart-text">
        <h4 className="drawer__cart-title">{Cart.name}</h4>
        <div className="drawer__cart-text-wrapper">
          <CheckData />
          <p className="drawer__cart-price">{Cart.price}</p>
        </div>
      </div>

      <button className="drawer__button-remove"></button>
    </div>
  );
};
export default DrawerCart;
