// store.js

import { makeAutoObservable } from "mobx";


class MyStore {
    data = [];
    jsonFiles = ['bigSize.json', 'boxs.json', 'chameleons.json', "cupsWithPhotos.json"];

    constructor() {
        makeAutoObservable(this)
    }

    async loadData() {
        try {
            const response = await jsonFiles.map(async (fileName) => {
                const response = await fetch(`/src/${fileName}`);
                const data = await response.json();
                return data;
            });
            // путь к вашему файлу
            const data = await response.json();
            this.data = data;
        } catch (error) {
            console.error('Error loading data:', error);
        }
    }
}

const myStore = new MyStore();
export default myStore;
