import axios from "axios";
import React from "react";
interface PropTypes {
  smallButton: boolean;
  Cart?: any;
  action: string;
  onLike: any;
  increment?: any;
  decrement?: any;
}
const DrawerCart: React.FC<PropTypes> = ({
  smallButton,
  Cart,
  onLike,
  action,
  increment,
  decrement,
}) => {
  const handleClick = (
    element: React.MouseEvent<HTMLButtonElement>,
    action: string
  ) => {
    action === "like" ? onLike(element, Cart) : console.log("hello");
    // Оновити карточку та викликати функцію оновлення в CardList
  };

  const CheckData = () => {
    if (smallButton) {
      return (
        <>
          <p className="drawer__cart-minus" onClick={() => decrement(Cart)}>
            -
          </p>
          <p className="drawer__cart-counter">{Cart.count ? Cart.count : 0}</p>
          <p className="drawer__cart-plus" onClick={() => increment(Cart)}>
            +
          </p>
        </>
      );
    } else {
      return null;
    }
  };
  return (
    <div className="drawer__cart">
      <div className="drawer__cart-img-block">
        <img
          src={`${process.env.PUBLIC_URL}/img/${Cart.images[0]}`}
          className="drawer__cart-img"
        />
      </div>
      <div className="drawer__cart-text">
        <h4 className="drawer__cart-title">{Cart.name}</h4>
        <div className="drawer__cart-text-wrapper">
          <CheckData />
          <p className="drawer__cart-price">{Cart.price}</p>
        </div>
      </div>

      <button
        onClick={(e) => handleClick(e, action)}
        className="drawer__button-remove"
      ></button>
    </div>
  );
};
export default DrawerCart;
