import { Route, Routes } from "react-router-dom";
import React, { useEffect, useState } from "react";

import Header from "./components/header";
import Footer from "./components/footer";
import Feedback from "./components/feedback";
import Autorization from "./pages/autorization/AuthPage";
import PageTemplate from "./pages/PageTemplate";
import Search from "./components/search";
import CartPage from "./pages/CartPage";
import HomePage from "./pages/HomePage";
import OnLikeText from "./components/onLikeText";

import "./style.scss";
import "swiper/css";
import "swiper/css/navigation";
import "swiper/css/pagination";

interface Cup {
  id: number;
  name: string;
  isLike: boolean;
  isAdd: boolean;
}

const App = () => {
  const [likedCart, setLikeCarts] = useState<any[]>([]);
  const [addedCart, setAddedCarts] = useState<any[]>([]);
  const [data, setData] = useState([]);
  const [withPhoto, setWithPhoto] = useState([]);
  const [chameleon, setChameleon] = useState([]);
  const [bigSize, setBigSize] = useState([]);
  const [boxs, setBoxs] = useState([]);
  const [popular, setPopular] = useState([]);

  const getAllData = async () => {
    const dataUrl = [
      "boxs.json",
      "bigSize.json",
      "chameleons.json",
      "cupsWithPhotos.json",
      "mostPopularCarts.json",
    ];

    const fetchData: any = async (fileName: string) => {
      const response = await fetch(
        `${process.env.PUBLIC_URL}/data/${fileName}`
      );
      const data = await response.json();
      return data;
    };

    const loadDataFromFiles = async () => {
      const allData = await Promise.all(dataUrl.map(fetchData));
      return allData;
    };
    const processData = (data: any, category: any) => {
      return data.map((product: any) => ({
        ...product,
        category,
      }));
    };

    const mergeData = async () => {
      try {
        const allData = await loadDataFromFiles();

        const mergedData: any = allData.flatMap((data, index) => {
          const category = `Category ${index + 1}`;
          return processData(data, category);
        });

        // Фильтрация массива с помощью filter
        const withPhotoFilter = mergedData.filter(
          (element: any) => element.category === "Category 4"
        );
        const chameleonFilter = mergedData.filter(
          (element: any) => element.category === "Category 3"
        );
        const bigSizeFilter = mergedData.filter(
          (element: any) => element.category === "Category 2"
        );
        const boxFilter = mergedData.filter(
          (element: any) => element.category === "Category 1"
        );
        const popularFilter = mergedData.filter(
          (element: any) => element.category === "Category 5"
        );
        // Установите данные в стейт, например, в useState
        setData(mergedData);
        setChameleon(chameleonFilter);
        setWithPhoto(withPhotoFilter);
        setBigSize(bigSizeFilter);
        setBoxs(boxFilter);
        setPopular(popularFilter);
      } catch (error) {
        console.error("Error merging and setting data:", error);
      }
    };

    await mergeData();
  };
  useEffect(() => {
    getAllData();
  }, []);
  useEffect(() => {
    console.log(data);
  }, [data]);

  const updateCardsLike = (updatedCard: any, funcUpdateState: any) => {
    funcUpdateState((prevData: any) =>
      prevData.map((card: any) =>
        card.id === updatedCard.id ? updatedCard : card
      )
    );
  };

  const onLikeClick = (
    event: React.MouseEvent<HTMLButtonElement>,
    cart: any,
    setCart: any,
    chagedState: any
  ) => {
    event.preventDefault();
    const updatedCart = { ...cart, isLike: !cart.isLike };
    setCart(updatedCart);
    updateCardsLike(updatedCart, chagedState);
    filterLike(updatedCart);
  };
  const filterLike = (updatedCart: any) => {
    if (updatedCart.isLike) {
      setLikeCarts((prevData: any) => [...prevData, updatedCart]);
    } else {
      setLikeCarts((prevData: any) =>
        prevData.filter((cart: any) => cart.id !== updatedCart.id)
      );
    }
  };

  const updateCardAdd = (updatedCard: any) => {
    console.log("hello");
  };

  return (
    <div className="App">
      {likedCart.length > 0 ? (
        <OnLikeText name={likedCart[likedCart.length - 1].name} />
      ) : null}
      <Header addedCart={addedCart} likedCart={likedCart} />
      <Routes>
        <Route
          path=""
          element={
            <HomePage
              popular={popular}
              boxs={boxs}
              bigSize={bigSize}
              updateCardAdd={updateCardAdd}
              updateCardLike={updateCardsLike}
              onLikeClick={onLikeClick}
            />
          }
        />
        <Route
          path="/cart/:id"
          element={
            <CartPage
              cards={data}
              onLikeClick={onLikeClick}
              updateCardAdd={updateCardAdd}
              updateCardLike={updateCardsLike}
            />
          }
        />
        <Route path="auth" element={<Autorization />} />
        <Route path="registration" element={<Autorization />} />

        <Route
          path="cupsWidthPhotos"
          element={
            <PageTemplate
              carts={withPhoto}
              onLikeClick={onLikeClick} //функція лайку
              updateCardAdd={updateCardAdd} //непотрібна поки
              isShowCarts={true}
              isShowLables={false}
              isShowText={false}
              updateCardLike={updateCardsLike}
            />
          }
        />
        <Route
          path="chameleon"
          element={
            <PageTemplate
              carts={chameleon}
              onLikeClick={onLikeClick} //функція лайку
              updateCardAdd={updateCardAdd}
              updateCardLike={updateCardsLike}
              isShowCarts={true}
              isShowLables={false}
              isShowText={false}
            />
          }
        />
        <Route
          path="BigSize"
          element={
            <PageTemplate
              carts={bigSize}
              onLikeClick={onLikeClick} //функція лайку
              updateCardAdd={updateCardAdd}
              updateCardLike={updateCardsLike}
              isShowCarts={true}
              isShowLables={false}
              isShowText={false}
            />
          }
        />
        <Route
          path="Boxs"
          element={
            <PageTemplate
              carts={boxs}
              onLikeClick={onLikeClick} //функція лайку
              isShowCarts={true}
              isShowLables={true}
              isShowText={false}
              updateCardAdd={updateCardAdd}
              updateCardLike={updateCardsLike}
            />
          }
        />
        <Route
          path="Delivery"
          element={
            <PageTemplate
              updateCardAdd={updateCardAdd}
              updateCardLike={updateCardsLike}
              isShowCarts={false}
              isShowLables={false}
              isShowText={true}
              title="Про доставку"
              description="Намагаємось доставити ваше замовлення так швидко, як котик біжить як чує звук відкриття холодильнику.
              Оформлені до 18:00 замовлення відправляємо в той же день!
              Доставка Новою Поштою 1-3 дні. Оплатити можна при отриманні на пошті, або по передплаті на карту."
            />
          }
        />
        <Route
          path="AboutUs"
          element={
            <PageTemplate
              updateCardAdd={updateCardAdd}
              updateCardLike={updateCardsLike}
              isShowCarts={false}
              isShowLables={false}
              isShowText={true}
              title="Про нас"
              description="Колись я оновлю цей блок і тут буде інформація про команду і те що ми зробили за весь час."
            />
          }
        />
      </Routes>

      <Feedback />
      <Search />
      <Footer />
    </div>
  );
};

export default App;
