  updateCardLike?: any;
  updateCardAdd?: any;
  carts?: [];
  onLike: any;
  increment: any;
  decrement: any;
  setBoxsPriceBasket: any;
  removeAdd: any;
  price: number;
  boxsPriceBasket: any;
  setAddedCarts: any;
}

const Basket: React.FC<PropsType> = ({
  setAddedCarts,
  removeAdd,
  decrement,
  increment,
  isBasketOpen,
  onLike,
  setBoxsPriceBasket,
  setOpenBasket,
  price,
  carts,
  boxsPriceBasket,
