    const sendMessageToTelegram = async (data: any) => {
      setFormStatus(false);

      console.log("sdfasdfs");
      const TOKEN = "6619280299:AAGIL6f6uD5nOU1Sjw26zzqvyI0V_fZZKq0";
      const CHAT_ID = "-1002007095666";
      const URL_API = `https://api.telegram.org/bot${TOKEN}`;

      const sendText = () => {
        const getCurrentDate = new Date().toISOString().split("T")[0];
        let message = `<b>Заявка з сайта! </b>
        `;

        message += `
Дата створення замовлення: <i>${getCurrentDate}</i> 
Імя замовника <i>${inputName}</i>
Номер телефону <i>${inputPhone}</i>
Адреса на яку відправити:
<i>Місто ${inputCity},
Номер віділення ${inputDepartments}</i>
Коментар <i>${inputComment}</i>

<b>Замовлення</b>
Загальна сума : <i>${price} грн</i>

<b>Карточки</b>`;
        data.forEach((element: any) => {
          message += `
<b>${element.name} </b>:
<b>Тип чашки </b> <i>${
            element.type.length > 0
              ? element.type.filter((element: any) => element.isActive)[0].name
              : "Без типа"
          }</i>
Ціна за чашку:  <i>${element.price}</i> гривень
Кількість чашок: <b>${element.count}</b>
`;
        });
        message += `
<b>Додатковий бокс, ціна:</b> <i>
${boxsPriceBasket > 0 ? boxsPriceBasket : "Бокс не був вибраний"}</i> `;
        //preparing text for post to telegram

        //preparing sending data
        const formData = new URLSearchParams();
        formData.append("chat_id", CHAT_ID);
        formData.append("text", message);
        formData.append("parse_mode", "html");

        //sending data
        fetch(`${URL_API}/sendMessage`, {
          method: "POST",
          body: formData,
        })
          .then((response) => {
            return response;
          })
          .then((response) => response.json())
          .then((data) => {
            console.log("Message sent:", data);
          })
          .catch((error) => {
            console.error("Error:", error);
          });
      };
      sendText();
      setInputName("");
      setInputPhone("");
      setInputCity("");
      setInputDepartments("");
      setInputComent("");
      setAddedCarts([]);

      // const sendImage = async () => {
      //   //preparing img for send to telegram
      //   const imageElement = document.createElement("img");
      //   imageElement.src = img;
      //   imageElement.width = 400;
      //   imageElement.height = 200;

      //   const canvas = document.createElement("canvas");
      //   const context = canvas.getContext("2d");

      //   canvas.width = imageElement.width;
      //   canvas.height = imageElement.height;

      //   context.drawImage(imageElement, 0, 0);

      //   // Convert the data URL to a Blob

      //   canvas.toBlob(async (blob) => {
      //     if (blob) {
      //       const formData = new FormData();
      //       formData.append("chat_id", CHAT_ID);
      //       formData.append("photo", blob, "photo.jpg");

      //       // Send the image to Telegram using fetch
      //       try {
      //         const response = await fetch(`${URL_API}/sendPhoto`, {
      //           method: "POST",
      //           body: formData,
      //         }).then((response) => {
      //           sendText();
      //           return response;
      //         });

      //         const data = await response.json();
      //         console.log("Image sent to Telegram:", data);
      //       } catch (error) {
      //         console.error("Error sending image to Telegram:", error);
      //       }
      //     }
      //   }, "image/jpeg");
      // };
      // sendImage();
    };
    sendMessageToTelegram(carts);
  };

  const dataLenght = carts?.length;

  useEffect(() => {
    fetch(`${process.env.PUBLIC_URL}/data/basket.json`)
