          ></span>
        </h2>
        {dataLenght ? (
          <>
            <div className="drawer__carts-block">
              {carts.map((cart: any, index: number) => {
                return (
                  <DrawerCart
                    setOpenBasket={setOpenBasket}
                    removeAdd={removeAdd}
                    increment={increment}
                    onLike={onLike}
                    decrement={decrement}
                    Cart={cart}
                    smallButton={true}
                    action="add"
                    key={cart.id}
                  />
                );
              })}
