import React, { useEffect, useState } from "react";
import { observer } from "mobx-react";
import ToDoStore from "../store/calculator";
interface IComponent {
  store?: any;
  todo?: any;
}
const TodoList: React.FC<IComponent> = observer(({ store }) => {
  const onnewTodo = () => {
    //   store.addTodo("read MobX tutorial");
    //   store.addTodo("try MobX");
    store.addTodo(prompt("Enter a new todo:", "coffee plz"));
  };

  return (
    <div>
      {store.report}
      <ul>
        {store.todos.map((todo: any, idx: number) => (
          <TodoView todo={todo} key={idx} />
        ))}
      </ul>
      {store.pendingRequests > 0 ? <mark>Loading </mark> : null}
      <button onClick={onnewTodo}>New Todo</button>
      <small>(doudle-click a todo to edit)</small>
    </div>
  );
});
const TodoView: React.FC<IComponent> = observer(({ todo }) => {
  const onToggleCompleted = () => {
    todo.completed = !todo.completed;
  };
  const onRename = () => {
    todo.task = prompt("Task name", todo.task) || todo.task;
  };
  return (
    <li onDoubleClick={onRename}>
      <input
        type="checkbox"
        checked={todo.completed}
        onChange={onToggleCompleted}
      />
      {todo.task}
      {todo.assignee ? <small>{todo.assignee.name}</small> : null}
    </li>
  );
});
export default TodoList;
