import React, { useEffect, useState } from "react";

interface PaymentRequest {
  amount: number;
  description: string;
  accountId: string; // Your Monobank account ID
  apiKey: string; // Your Monobank API key
}

const PaymentComponent: React.FC = () => {
  const [text, setText] = useState("");

  const getDataFromMonoUser = () => {
    fetch("https://api.monobank.ua/api/merchant/invoice/create", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "X-Token": "utR_bnF6LUzdc4pr3yFNFF2kKEPk75xeIlItZx9QfaxY",
      },
      body: JSON.stringify({
        amount: 1000,
        redirectUrl: "https://mister-gifter.com/",
      }),
    })
      .then((response) => {
        console.log(response);
        return response.json();
      })
      .then((data) => console.log(data))
      .catch((response) => console.log(response));

    fetch("https://api.monobank.ua/personal/client-info", {
      method: "GET",
      headers: {
        "X-Token": "mT_v1FyCGBuRbblh694K58Q",
      },
    }).then((response) => console.log(response.json()));
  };

  return (
    <div style={{ textAlign: "center", marginTop: "100px" }}>
      <h1>Привіт</h1>
      <p> тут тестую monopay</p>
      <p> </p>
      <button onClick={getDataFromMonoUser}>Get Data</button>
    </div>
  );
};

export default PaymentComponent;
