import React, { useEffect, useState } from "react";
import style from "./autorization.module.scss";

interface componentTypes {
  token: string;
  setToken: (token: string) => void;
}

const Autorization: React.FC<componentTypes> = ({ token, setToken }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [currentURL, setUrl] = useState("");
  useEffect(() => {
    let url = window.location.pathname.split("/").pop();
    setUrl(url);
  }, []);
  const Show = () => {
    if (currentURL === "registration") {
      return (
        <div className={style.autorization}>
          <h2 className={style.autorization__title}>Зареєструватись</h2>
          <input
            className={style.autorization__input}
            type="email"
            placeholder="Ведіть ваш email..."
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <input
            className={style.autorization__input}
            type="password"
            value={password}
            placeholder="Ведіть ваш пароль..."
            onChange={(e) => setPassword(e.target.value)}
          />
          <p className={style.autorization__text}>
            Є аккаунт?{" "}
            <button className={style.autorization__span}>Увійти</button>
          </p>
          <button
            className={style.autorization__button}
            onClick={() => sendDataReg(email, password)}
          >
            Увійти
          </button>
        </div>
      );
    } else if (currentURL === "autorization") {
      return (
        <div className={style.autorization}>
          <h2 className={style.autorization__title}>Авторизація</h2>
          <input
            className={style.autorization__input}
            type="email"
            placeholder="Ведіть ваш email..."
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <input
            className={style.autorization__input}
            type="password"
            value={password}
            placeholder="Ведіть ваш пароль..."
            onChange={(e) => setPassword(e.target.value)}
          />
          <p className={style.autorization__text}>
            Немає аккаунта?{" "}
            <button className={style.autorization__span}>
              Зареєструватись
            </button>
          </p>
          <button
            className={style.autorization__button}
            onClick={() => sendDataReg(email, password)}
          >
            Увійти
          </button>
        </div>
      );
    }
  };

  const sendDataReg = (email: string, password: string) => {
    if (!(email.length > 4) || !(password.length > 4)) {
      return;
    }
    const url = "http://localhost:5000/api/user/registration";
    fetch(url, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ email, password }),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        console.log("Success:", data);
        setToken(data);
        setEmail("");
        setPassword("");
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  };

  return <Show></Show>;
};
export default Autorization;
