import React, { useState } from "react";

const QuestionLabel = ({ title, description }) => {
  const [isOpen, setOpen] = useState(false);
  return (
    <div className="questions__block">
      <h3
        className={
          isOpen ? "questions__text questions__text--active" : "questions__text"
        }
        onClick={() => setOpen((status) => !status)}
      >
        {title}
      </h3>
      <p
        className={
          isOpen
            ? "questions__description--active questions__description"
            : "questions__description"
        }
      >
        {description}
      </p>
    </div>
  );
};
export default QuestionLabel;
