import { Route, Routes } from "react-router-dom";
import axios from "axios";
import { useEffect, useState } from "react";

import Header from "./components/header";
import Footer from "./components/footer";
import Feedback from "./components/feedback";
import Autorization from "./pages/autorization/AuthPage";
import PageTemplate from "./pages/PageTemplate";
import Search from "./components/search";
import CartPage from "./pages/CartPage";
import HomePage from "./pages/HomePage";
import OnLikeText from "./components/onLikeText";

import "./style.scss";
import "swiper/css";
import "swiper/css/navigation";
import "swiper/css/pagination";

interface Cup {
  id: number;
  name: string;
  isLike: boolean;
  isAdd: boolean;
}

const App = () => {
  const [token, setToken] = useState("");
  const [mockData, setData] = useState([]);
  const [likeMock, setLikeMock] = useState<Cup[]>([]);
  const [addMock, setAddMock] = useState([]);
  const [counterLike, setLikeCouter] = useState(0);
  const [counterAdd, setAddCouter] = useState(0);
  const [mockBoxs, setMockBoxs] = useState([]);
  const [mockBigSize, setMockBigSize] = useState([]);
  const [mockChameleon, setMockChameleon] = useState([]);
  const [mockWithPhoto, setMockWithPhoto] = useState([]);
  const [mockMostPopular, setMockMostPopular] = useState([]);
  const [openedCart, setOpenedCart] = useState([]);

  useEffect(() => {
    const boxsesMock: [] = require("./data/boxs.json");
    const bigSizeMock: [] = require("./data/bigSize.json");
    const chameletonMock: [] = require("./data/chameleons.json");
    const withPhotoMock: [] = require("./data/cupsWithPhotos.json");
    setMockBoxs(boxsesMock);
    setMockBigSize(bigSizeMock);
    setMockChameleon(chameletonMock);
    setMockWithPhoto(withPhotoMock);
    setMockMostPopular([...withPhotoMock, ...chameletonMock, ...boxsesMock]);
    console.log(mockMostPopular);
  }, []);

  useEffect(() => {
    axios
      .get("https://65646353ceac41c0761e1278.mockapi.io/data")
      .then((response) => setData(response.data))
      .then((data) => {
        const filterLikedItems = (items: any) => {
          return items.filter((item: any) => item.isLike);
        };

        const filterAddItems = (items: any) => {
          return items.filter((item: any) => item.isAdd);
        };

        setLikeMock(filterLikedItems(data));
        setLikeCouter(filterLikedItems(data).length);

        setAddMock(filterAddItems(data));
        setAddCouter(filterAddItems(data).length);
      })
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  useEffect(() => {
    const filterLikedItems = (items: any) => {
      return items.filter((item: any) => item.isLike);
    };
    setLikeMock(filterLikedItems(mockData));
    console.log(likeMock, "liked");
    setLikeCouter(filterLikedItems(mockData).length);
  }, [mockData]);

  useEffect(() => {
    const filterAddItems = (items: any) => {
      return items.filter((item: any) => item.isAdd);
    };
    setAddMock(filterAddItems(mockData));
    setAddCouter(filterAddItems(mockData).length);
    console.log(addMock, "added");
  }, [mockData]);

  const updateCardLike = (updatedCard: any) => {
    // Оновити стан списку карт з оновленою карточкою
    setData((prevData: any) =>
      prevData.map((card: any) =>
        card.id === updatedCard.id ? updatedCard : card
      )
    );
  };
  const updateCardAdd = (updatedCard: any) => {
    // Оновити стан списку карт з оновленою карточкою
    setData((prevData: any) =>
      prevData.map((card: any) =>
        card.id === updatedCard.id ? updatedCard : card
      )
    );
  };
  console.log(+null);

  return (
    <div className="App">
      {likeMock.length > 0 ? (
        <OnLikeText name={likeMock[likeMock.length - 1].name} />
      ) : null}
      <Header
        counterAdd={counterAdd}
        counterLike={counterLike}
        updateCardAdd={updateCardAdd}
        addMock={addMock}
        likeMock={likeMock}
        updateCardLike={updateCardLike}
      />
      <Routes>
        <Route
          path=""
          element={
            <HomePage
              updateCardAdd={updateCardAdd}
              updateCardLike={updateCardLike}
              mockBigSize={mockBigSize}
              mockBoxs={mockBoxs}
              mockMostPopular={mockMostPopular}
              setOpenedCart={setOpenedCart}
            />
          }
        />
        <Route
          path="/cart/:id"
          element={
            <CartPage
              setOpenedCart={setOpenedCart}
              openedCart={openedCart}
              updateCardAdd={updateCardAdd}
              cards={mockMostPopular}
              setData={setData}
              updateCardLike={updateCardLike}
              like={likeMock}
              setLikeMock={setLikeMock}
              addMock={addMock}
              setAddMock={setAddMock}
            />
          }
        />
        <Route
          path="auth"
          element={<Autorization token={token} setToken={setToken} />}
        />
        <Route
          path="registration"
          element={<Autorization token={token} setToken={setToken} />}
        />
        <Route
          path="cupsWidthPhotos"
          element={
            <PageTemplate
              setOpenedCart={setOpenedCart}
              updateCardAdd={updateCardAdd}
              data={mockWithPhoto}
              isShowCarts={true}
              isShowLables={false}
              isShowText={false}
              updateCardLike={updateCardLike}
            />
          }
        />
        <Route
          path="chameleon"
          element={
            <PageTemplate
              updateCardAdd={updateCardAdd}
              setOpenedCart={setOpenedCart}
              updateCardLike={updateCardLike}
              data={mockChameleon}
              isShowCarts={true}
              isShowLables={false}
              isShowText={false}
            />
          }
        />
        <Route
          path="BisSize"
          element={
            <PageTemplate
              updateCardAdd={updateCardAdd}
              setOpenedCart={setOpenedCart}
              updateCardLike={updateCardLike}
              data={mockBigSize}
              isShowCarts={true}
              isShowLables={false}
              isShowText={false}
            />
          }
        />
        <Route
          path="Boxs"
          element={
            <PageTemplate
              data={mockBoxs}
              isShowCarts={true}
              isShowLables={true}
              setOpenedCart={setOpenedCart}
              isShowText={false}
              updateCardAdd={updateCardAdd}
              updateCardLike={updateCardLike}
            />
          }
        />
        <Route
          path="Delivery"
          element={
            <PageTemplate
              updateCardAdd={updateCardAdd}
              updateCardLike={updateCardLike}
              data={mockData}
              isShowCarts={false}
              isShowLables={false}
              isShowText={true}
              title="Про доставку"
              description="Намагаємось доставити ваше замовлення так швидко, як котик біжить як чує звук відкриття холодильнику.
              Оформлені до 18:00 замовлення відправляємо в той же день!
              Доставка Новою Поштою 1-3 дні. Оплатити можна при отриманні на пошті, або по передплаті на карту."
            />
          }
        />
        <Route
          path="AboutUs"
          element={
            <PageTemplate
              updateCardAdd={updateCardAdd}
              updateCardLike={updateCardLike}
              data={mockData}
              isShowCarts={false}
              isShowLables={false}
              isShowText={true}
              title="Про нас"
              description="Колись я оновлю цей блок і тут буде інформація про команду і те що ми зробили за весь час."
            />
          }
        />
      </Routes>

      <Feedback />
      <Search />
      <Footer />
    </div>
  );
};

export default App;
