import { Swiper, SwiperSlide } from "swiper/react";
import { Navigation, Pagination, Autoplay, Keyboard } from "swiper/modules";
import style from "./feedback.module.scss";

const Feedback = () => {
  return (
    <section className={style.feedback}>
      <h2 className={`title ${style.feedback__title} `}>
        Відгуки наших клієнтів
      </h2>
      <Swiper
        className={style.feedback__slider}
        modules={[Navigation, Pagination, Autoplay, Keyboard]}
        slidesPerView={1}
        spaceBetween={50}
        navigation={{
          nextEl: `${style.swiper_button_next}`,
          prevEl: `${style.swiper_button_prev}`,

        }}
        loop={true}
        keyboard={{ enabled: true }}
        autoplay={{ delay: 5000 }}
        pagination={{ clickable: true }}
      >
        <SwiperSlide className={style.feedback__slide}>
          <img src={`${process.env.PUBLIC_URL}/img/feedback/feedback1.jpeg`} />
        </SwiperSlide>
        <SwiperSlide className={style.feedback__slide}>
          <img src={`${process.env.PUBLIC_URL}/img/feedback/feedback1.jpeg`} />
        </SwiperSlide>
        <SwiperSlide className={style.feedback__slide}>
          <img src={`${process.env.PUBLIC_URL}/img/feedback/feedback1.jpeg`} />
        </SwiperSlide>
        <SwiperSlide className={style.feedback__slide}>
          <img src={`${process.env.PUBLIC_URL}/img/feedback/feedback1.jpeg`} />
        </SwiperSlide>
        <div className={style.swiper_button_next}></div>
        <div className={style.swiper_button_prev}></div>
      </Swiper>
    </section>
  );
};
export default Feedback;
