import React, { useState, useEffect } from "react";
import QuestionLabel from "src/components/question";
import Slider from "src/components/slider";
import { Swiper, SwiperSlide, useSwiper, SwiperRef } from "swiper/react";
import { useParams } from "react-router-dom";
import axios from "axios";

import {
  Navigation,
  Pagination,
  Scrollbar,
  A11y,
  EffectFade,
  Controller,
} from "swiper/modules";
import "swiper/css/navigation";
import "swiper/css/pagination";
import "swiper/css/scrollbar";

interface MyComponentProps {
  cards: any;
  setData: any;
  like: any;
  setLikeMock: any;
  addMock: any;
  setAddMock: any;
}

const CartPage: React.FC<MyComponentProps> = ({
  cards,
  setData,
  like,
  setLikeMock,
  addMock,
  setAddMock,
}) => {
  const { id } = useParams();
  const [card, setCard] = useState({
    id: "",
    name: "",
    price: 0,
    oldPrice: 0,
    description: "",
    about: "",
    imgOrdered: [],
    images: [],
    type: [],
  });

  useEffect(() => {
    axios
      .get(`https://65646353ceac41c0761e1278.mockapi.io/data/${id}`)
      .then((response) => setCard(response.data))
      .catch((error) => console.error("Error fetching card details:", error));
  }, [id]);

  const ShowOrderedCups = () => {
    if (card.imgOrdered === undefined) {
      return null;
    }
    return card.imgOrdered.map((imgUrl: string, index: number) => {
      return (
        <img
          className="CartPage__orderedImg"
          src={`${process.env.PUBLIC_URL}/img/carts/${imgUrl}`}
          key={index}
        />
      );
    });
  };

  const ShowCheckBoxs = () => {
    const ShowActiveBoxs = () => {
      return card.type.map((element: any, index: number) => {
        if (element.isActive) {
          return (
            <>
              <h3 className="CartPage__checkBoxs_title">Чашка:</h3>
              <div className="CartPage__checkBoxs_active" key={index}>
                <img
                  className="CartPage__checkBoxs_img"
                  src={`${process.env.PUBLIC_URL}/img/carts/${element.img}`}
                />
                <h3 className="CartPage__checkBoxs_title">{element.name}</h3>
              </div>
            </>
          );
        }
      });
    };
    const ShowOther = () => {
      return card.type.map((element: any, index: number) => {
        return (
          <div
            className={
              element.isActive
                ? "CartPage__checkBoxs_row CartPage__checkBoxs_row--active "
                : "CartPage__checkBoxs_row "
            }
            key={index}
          >
            <img
              className="CartPage__checkBoxs_img"
              src={`${process.env.PUBLIC_URL}/img/carts/${element.img}`}
            />
            <h3 className="CartPage__checkBoxs_title">{element.name}</h3>
          </div>
        );
      });
    };
    return (
      <div className="CartPage__checkBoxs_block">
        <ShowActiveBoxs />
        <div className="CartPage__checkBoxs_wrapper CartPage__checkBoxs_wrapper--active">
          <ShowOther />
        </div>
      </div>
    );
  };

  interface CustomPaginationProps {
    count: number;
    activeIndex: number;
    onDotClick: (index: number) => void;
  }
  const [activeIndex, setActiveIndex] = useState(0);

  const CustomPagination: React.FC<CustomPaginationProps> = ({
    count,
    onDotClick,
  }) => {
    const swiper = useSwiper<SwiperRef>(); // useSwiper hook to get access to the Swiper instance
    const dots = Array.from({ length: count }, (_, index) => (
      <span
        key={index}
        onClick={() => {
          onDotClick(index);
          swiper.slideTo(index); // Slide to the clicked index
        }}
        className={`dot ${index === swiper.activeIndex ? "active" : ""}`}
      />
    ));

    return <div className="pagination">{dots}</div>;
  };

  const handleSlideChange = (swiper: typeof Swiper) => {
    setActiveIndex(swiper.activeIndex);
  };

  return (
    <div className="CartPage">
      <Swiper
        modules={[Navigation, Pagination, Scrollbar, EffectFade]}
        effect="fade"
        onSlideChange={handleSlideChange}
        className="CartPage__slider"
        spaceBetween={70}
        slidesPerView={1}
        navigation
        pagination={{ clickable: true }}
      >
        {card.images.map((img: string, index: number) => {
          return (
            <SwiperSlide className="CartPage__slide" key={index}>
              <img
                className="CartPage__img"
                src={`${process.env.PUBLIC_URL}/img/carts/${img}`}
              />
            </SwiperSlide>
          );
        })}
        <CustomPagination
          count={totalSlides}
          activeIndex={activeIndex}
          onDotClick={setActiveIndex}
        />
      </Swiper>
      <h2 className="CartPage__title">{card.name}</h2>
      <div className="CartPage__row">
        <span className="CartPage__price">{card.price} ₴</span>
        <span className="CartPage__oldPrice">{card.oldPrice} ₴</span>
      </div>
      <ShowCheckBoxs />
      <div className="CartPage__row">
        <button className="CartPage__button">Замовити</button>
        <button className="CartPage__like">
          <img src="img/heart-svgrepo-com.svg" />
        </button>
      </div>
      <p
        className="CartPage__text"
        dangerouslySetInnerHTML={{ __html: card.description }}
      ></p>
      <div className="CartPage__block">
        <h2 className="CartPage__block_title">
          Чашки, які вже замовили з цим дизайном
        </h2>
        <div className="CartPage__block_wrapper">
          <ShowOrderedCups />
        </div>
      </div>
      <QuestionLabel title="Детальніше про продукт" description={card.about} />
      <QuestionLabel
        title="Доставка і оплата"
        description={`Оформлені до 18:00 замовлення відправляємо в той же день!\n
Доставка Новою Поштою 1-3 дні\n
Оплатити замовлення можна при отриманні на Новій пошті (*крім кастомних чашок), або по передплаті на банківський рахунок.`}
      />
      <Slider />
    </div>
  );
};
export default CartPage;
