import React, { useEffect, useState } from "react";
import DrawerCart from "../drawerCart";
import { Link } from "react-router-dom";

interface PropsType {
  isLikeOpen?: boolean | undefined;
  setOpenLike?: (status: boolean) => void;
  isBasketOpen?: boolean | undefined;
  setOpenBasket?: (status: boolean) => void;
  updateCardLike?: any;
  updateCardAdd?: any;
  carts?: [];
}
interface header {
  likeMock: any;
  updateCardLike: any;
  addMock: any;
  updateCardAdd: any;
  counterLike: number;
  counterAdd: number;
}
interface Like {
  likeMock: any;
  setOpenLike: any;
  classeForButton: any;
  counter: number;
}

interface Add {
  setOpenAdd: any;
  classeForButton: any;
  counter: number;
}

const LikedCart: React.FC<PropsType> = ({
  isLikeOpen,
  setOpenLike,
  carts,
  updateCardLike,
}) => {
  const dataLenght = carts?.length;
  return (
    <section className={isLikeOpen ? "like like--active" : "like"}>
      <div className="like__wrapper">
        <h2 className="like__title">
          Уподобане:
          <span
            className="like__exit "
            onClick={() => setOpenLike && setOpenLike(!isLikeOpen)}
          ></span>
        </h2>

        {dataLenght ? (
          <div className="like__block">
            {carts.map((cart: any, index: number) => {
              return (
                <DrawerCart
                  updateCard={updateCardLike}
                  Cart={cart}
                  smallButton={false}
                  action="like"
                  key={index}
                />
              );
            })}
          </div>
        ) : (
          <div className="like__nothing">
            <h2 className="like__text">Ви ще нічого не вподобали</h2>
            <button
              className="like__button"
              onClick={() => setOpenLike && setOpenLike(!isLikeOpen)}
            >
              ВПОДОБАТИ
            </button>
          </div>
        )}
      </div>
    </section>
  );
};

const Basket: React.FC<PropsType> = ({
  isBasketOpen,
  updateCardAdd,
  setOpenBasket,
  carts,
}) => {
  const [number, setNumber] = useState("");
  const dataLenght = carts?.length;
  return (
    <section className={isBasketOpen ? "drawer drawer--active" : "drawer"}>
      <div className="drawer__wrapper">
        <h2 className="drawer__title">
          Ваше замовлення:{" "}
          <span
            className="drawer__exit "
            onClick={() => setOpenBasket && setOpenBasket(!isBasketOpen)}
          ></span>
        </h2>
        {dataLenght ? (
          <>
            <div className="drawer__carts-block">
              {carts.map((cart: any, index: number) => {
                return (
                  <DrawerCart
                    updateCard={updateCardAdd}
                    Cart={cart}
                    smallButton={true}
                    action="add"
                    key={index}
                  />
                );
              })}
            </div>
            <div className="drawer__price">Сума 898$</div>
            <div className="drawer__description">
              Додай бокс для вау ефекту! ✨⬇️
            </div>
            <div className="drawer__row">
              <input
                id="drawer-box-checkbox1"
                className="drawer__box-checkbox"
                type="radio"
                name="box"
              />
              <label htmlFor="drawer-box-checkbox1" className="drawer__box">
                <img
                  className="drawer__box-img"
                  src={`${process.env.PUBLIC_URL}/img/carts/low_price.jpg`}
                />
                <h5 className="drawer__box-title">Rafaello Box</h5>
              </label>
              <input
                id="drawer-box-checkbox2"
                className="drawer__box-checkbox"
                type="radio"
                name="box"
              />
              <label htmlFor="drawer-box-checkbox2" className="drawer__box">
                <img
                  src={`${process.env.PUBLIC_URL}/img/carts/low_price.jpg`}
                  className="drawer__box-img"
                />
                <h5 className="drawer__box-title">Rafaello Box</h5>
              </label>
              <input
                id="drawer-box-checkbox3"
                className="drawer__box-checkbox"
                type="radio"
                name="box"
              />
              <label htmlFor="drawer-box-checkbox3" className="drawer__box">
                <img
                  src={`${process.env.PUBLIC_URL}/img/carts/low_price.jpg`}
                  className="drawer__box-img"
                />
                <h5 className="drawer__box-title">Rafaello Box</h5>
              </label>
            </div>
            <h4 className="drawer__text">Прізвище та Імя:</h4>
            <input
              className="drawer__input"
              type="text"
              placeholder="Прізвище Імя По-батькові"
            />
            <h4 className="drawer__text">Телефон:</h4>
            <input
              className="drawer__input"
              type="text"
              placeholder="+380-99-999-99-99"
              value={number}
              onChange={(e) => setNumber(e.target.value)}
            />

            <h4 className="drawer__text">Місто:</h4>
            <input
              className="drawer__input"
              type="text"
              placeholder="м. Київ"
            />
            <h4 className="drawer__text">Відділення Нової Пошти:</h4>
            <input className="drawer__input" type="text" placeholder="№1" />
            <h4 className="drawer__text">Коментар:</h4>
            <input
              className="drawer__input"
              type="text"
              placeholder="Залиште комендар до свого замовлення"
            />
            <h4 className="drawer__text">Як з вами звязатись</h4>
            <label className="drawer__label">
              <input
                className="drawer__label-input"
                type="radio"
                name="connection"
              />
              <h5 className="drawer__label-title">Viber</h5>
            </label>
            <label className="drawer__label">
              <input
                className="drawer__label-input"
