import { useState } from "react";
import style from "./style.module.scss";

const Cart = ({ name, price, oldPrice, redText, images }) => {
  const [isLike, setLike] = useState(false);
  const [isAdd, setAdd] = useState(false);
  const [firstImg, secondImg] = images;
  return (
    <cart className={style.slider__cart}>
      <div className={style.slider__cart - img - block}>
        <img
          classList={style.slider__cart - img}
          src={`img/carts/${firstImg}`}
        />
        <img
          classList={style.slider__cart - img}
          src={`img/carts/${secondImg}`}
        />
      </div>
      <h3 className={style.slider__cart - title}>{name}</h3>
      <div className={style.slider__cart - price}>
        <span className={style.slider__cart - price - current}>{price}$</span>
        <span className={style.slider__cart - price - old}>{oldPrice}$</span>
      </div>
      <button
        className={style.slider__cart - button}
        onClick={() => setAdd((status) => !status)}
      >
        Додати в кошик
      </button>
      <button
        className={style.slider__cart - like}
        onClick={() => setLike((status) => !status)}
      >
        <img src="img/heart-svgrepo-com.svg" />
      </button>
      <span className={style.slider__cart - button - red}>{redText}</span>
    </cart>
  );
};

export default Cart;
