import React from "react";
import { createProxyMiddleware } from "http-proxy-middleware";

interface IComponent {
  addedCarts: any;
}

const SendSRM: React.FC<IComponent> = ({ addedCarts }) => {
  const onClick = async (e: React.MouseEvent<HTMLButtonElement>) => {
    const data = {
      source_id: 1,
      buyer: {
        full_name: "ivan",
        email: "ivan.kalunuch12@gmail.com",
        phone: "+380934638895",
      },
      shipping: {
        shipping_address_city: "Scole",
        shipping_receive_point: "Ivan Franko 42a",
        shipping_address_country: "Ukraine",
        shipping_address_region: "Lvivska Oblast",
        shipping_address_zip: "23333",
      },
      products: [
        {
          price: 1,
          quantity: 1,
          name: "Чашка ",
          picture:
            "https://thumb.tildacdn.com/stor3635-6363-4864-a664-353761303838/-/format/webp/91148690.png",
          properties: [
            {
              name: "Color",
              value: "хамелеон",
            },
          ],
        },
      ],
    };
    const dataString = JSON.stringify(data);
    const token = "ZTg5MWY5NDE4YWRkYTBjZDIzODQ0Mjc4NzRmYmM2MWQxMGY0MmQzZg";

    fetch("/api/order", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
        "Cache-Control": "no-cache",
        Pragma: "no-cache",
        Authorization: `Bearer ${token}`,
      },
      body: dataString,
    })
      .then((response) => response.json())
      .then((result) => {
        // Обробка результату від сервера KeyCRM
        console.log(result);
      })
      .catch((error) => {
        // Обробка помилки
        console.error("Помилка при виконанні запиту:", error);
      });
  };

  // Ваш унікальний API ключ KeyCRM

  // Використовуємо Fetch API для виконання HTTP-запиту

  return (
    <div style={{ marginTop: "200px", textAlign: "center" }}>
      <h2>Send CRM</h2>
      <button onClick={onClick}>send crm</button>
    </div>
  );
};
export default SendSRM;

const proxy = createProxyMiddleware("/api", {
  target: "./https://openapi.keycrm.app/v1",
  changeOrigin: true,
  pathRewrite: { "^/api": "" },
});
