import React from "react";
import QuestionLabel from "src/components/question";
import Slider from "src/components/slider";
import { Swiper, SwiperSlide } from "swiper/react";

interface MyComponentProps {
  cart: any;
  setData: any;
  like: any;
  setLikeMock: any;
  addMock: any;
  setAddMock: any;
}

const CartPage: React.FC<MyComponentProps> = ({
  cart,
  setData,
  like,
  setLikeMock,
  addMock,
  setAddMock,
}) => {
  console.log(cart);
  const ShowOrderedCups = () => {
    if (cart.imgOrdered.lenght <= 0) {
      return null;
    }
    return cart.imgOrdered.map((imgUrl: string) => {
      return <img src={imgUrl} />;
    });
  };
  const ShowCartImg = () => {
    return cart.images.map((img: string) => {
      return (
        <SwiperSlide>
          <img className="CartPage__img" src={img} />
        </SwiperSlide>
      );
    });
  };

  return (
    <div className="CartPage">
      <Swiper className="CartPage__slider">
        <ShowCartImg />
      </Swiper>
      <h2 className="CartPage__title">{cart.title}</h2>
      <div className="CartPage__row">
        <span className="CartPage__price">{cart.price}</span>{" "}
        <span className="CartPage__oldPrice">{cart.oldPrice}</span>
      </div>
      <div></div>
      <div className="CartPage__row">
        <button className="CartPage__button">Замовити</button>
        <button className="CartPage__like"></button>
      </div>
      <p></p>
      <div className="CartPage__block">
        <h2 className="CartPage__block_title">
          Чашки, які вже замовили з цим дизайном
        </h2>
        <ShowOrderedCups />
      </div>
      <QuestionLabel title="Детальніше про продукт" description={cart.about} />
      <QuestionLabel
        title="Доставка і оплата"
        description={`Оформлені до 18:00 замовлення відправляємо в той же день!\n
Доставка Новою Поштою 1-3 дні\n
Оплатити замовлення можна при отриманні на Новій пошті (*крім кастомних чашок), або по передплаті на банківський рахунок.`}
      />
      <Slider />
    </div>
  );
};
export default CartPage;
