import React, { useState } from "react";

interface PaymentRequest {
  amount: number;
  description: string;
  accountId: string; // Your Monobank account ID
  apiKey: string; // Your Monobank API key
}

const PaymentComponent: React.FC = () => {
  const [paymentRequest, setPaymentRequest] = useState<PaymentRequest>({
    amount: 100, // Amount in hryvnias
    description: "Service payment",
    accountId: "YOUR_ACCOUNT_ID", // Replace with your Monobank account ID
    apiKey: "YOUR_API_KEY", // Replace with your Monobank API key
  });

  const handlePayment = async () => {
    try {
      const response = await initiatePayment(paymentRequest);
      console.log("Payment initiated successfully:", response);
      // Redirect user to the payment page or handle the response accordingly
    } catch (error) {
      console.error("Error initiating payment:", error);
      // Handle errors, e.g., show an error message to the user
    }
  };

  const initiatePayment = async (request: PaymentRequest) => {
    const apiUrl = "https://api.monobank.ua/v2/bank/currency";

    const requestOptions: RequestInit = {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "X-Token": request.apiKey,
      },
      body: JSON.stringify({
        account: request.accountId,
        amount: request.amount * 100, // Amount in kopiykas
        description: request.description,
      }),
    };

    const response = await fetch(apiUrl, requestOptions);

    if (!response.ok) {
      throw new Error(`Payment initiation failed: ${response.statusText}`);
    }

    return response.json();
  };

  return (
    <div>
      <h1>Payment Page</h1>
      <p>Amount: {paymentRequest.amount} UAH</p>
      <button onClick={handlePayment}>Pay Now</button>
    </div>
  );
};

export default PaymentComponent;
