import Cart from "../cart";
import { Swiper, SwiperSlide } from "swiper/react";
import { Scrollbar, FreeMode } from "swiper/modules";
import { Link } from "react-router-dom";
import "swiper/css";
import "swiper/css/scrollbar";
import React, { ReactNode, useEffect, useState } from "react";
interface YourComponentProps {
  title?: string;
  data: Array<any>;
  onLike?: any;
  onAdd?: any;
}
const Slider: React.FC<YourComponentProps> = ({
  onAdd,
  title,
  data,
  onLike,
}) => {
  const [isMobile, setIsMobile] = useState<boolean>(window.innerWidth < 800);

  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth < 800);
    };

    window.addEventListener("resize", handleResize);

    // Initialize or destroy Swiper based on the screen width
    if (isMobile) {
      const swiper = new Swiper(".swiper-container", {
        modules: [FreeMode, Scrollbar],
        freeMode: true,
        slidesPerView: 1.3,
        spaceBetween: 35,
        scrollbar: {
          // Additional scrollbar options
        },
        // Additional mobile-specific options
      });

      return () => {
        swiper.destroy();
      };
    }

    return () => {
      // Cleanup for non-mobile view
    };
  }, [isMobile]);

  const showCarts = (): ReactNode[] => {
    return data.map((cart, index) => (
      <SwiperSlide key={index}>
        <Link to={`/cart/${cart.id}`}>
          <Cart carts={data} cart={cart} onAdd={onAdd} onLike={onLike} />
        </Link>
      </SwiperSlide>
    ));
  };
  return (
    <section className="popular">
      <h2 className="popular__title title">{title}</h2>
      <div className={isMobile ? "popular__slider slider" : "grid-container"}>
        {isMobile}
        <Swiper
          className="slider__row"
          modules={[FreeMode, Scrollbar]}
          freeMode={true}
          breakpoints={{
            0: {
              slidesPerView: 1.3,
              spaceBetween: 35,
            },
            580: {
              slidesPerView: 2.5,
              spaceBetween: 35,
            },
          }}
        >
          {showCarts()}
        </Swiper>
      </div>
    </section>
  );
};
export default Slider;
