import React, { ReactNode } from "react";
import Cart from "src/components/cart";
import QuestionLabel from "src/components/question";
interface PropsType {
  data: Array<any>;
}
const PageTemplate: React.FC<PropsType> = ({ data }) => {
  const showCarts = (): ReactNode[] => {
    return data.map(({ name, price, oldPrice, redText, images }, index) => (
      <Cart
        name={name}
        price={price}
        oldPrice={oldPrice}
        redText={redText}
        images={images}
        key={index}
      />
    ));
  };
  return (
    <div className="page__wrapper">
      {showCarts()}
      <QuestionLabel
        title="Детальніше про продукт"
        description="Детальніше про продукт
Подарунковий бокс, йде тільки в комплекті з Чашкою або Бокалом.
Гарантує успішну доставку, оскільки бокс окрім своєї краси, є іще і доволі міцним (В нової пошти немає шансів)
Бокс прикрашений декоративним сіном - всередині і подарунковою стрічку з бантиком - ззовні.

і ще багато води, бла бла бла... якщо щось не розписав, напишіть мені в телеграм, відповідаю швидко"
      />
    </div>
  );
};
export default PageTemplate;
