export interface RequestPaymentReq {
    LoginAccount: string;
    Amount: number;
    OrderId: number;
    CallBackUrl: string;
    AdditionalData: string;
}
export interface RequestPaymentRes {
    SalePaymentRequestResult: {
        Token?: number | string;
        Status: number;
        Message: string;
    };
}
export interface CallbackParams {
    Token: number | string;
    status: number | string;
    OrderId: number | string;
    TerminalNo: number | string;
    RRN: number | string;
    HashCardNumber: string;
    Amount: number;
}
export interface VerifyPaymentReq {
    LoginAccount: string;
    Token: number | string;
}
export interface VerifyPaymentRes {
    ConfirmPaymentResult: {
        Status: number | string;
        RRN: number;
        CardNumberMasked: string;
        Token: number | string;
    };
}
export type ReversalPaymentReq = VerifyPaymentReq;
export interface ReversalPaymentRes {
    Status: string;
    Token: number | string;
}
