export interface RequestPaymentReq {
    apiKey: string;
    amount: number;
    callbackUrl: string;
    mobileNumber?: string;
    cardNumber?: string;
    invoiceId?: string;
}
export interface RequestPaymentRes_Success {
    paymentUrl: string;
    approvalUrl: string;
    paymentId: number;
    invoiceId: string | null;
    message: string;
    statusCode: number;
}
export interface RequestPaymentRes_Failed {
    message: string | null;
    errorCode: number;
    statusCode: number;
}
export type RequestPaymentRes = RequestPaymentRes_Success | RequestPaymentRes_Failed;
export interface CallbackParams {
    paymentId: number;
    isSucceed: boolean;
}
export interface VerifyPaymentReq {
    apiKey: string;
    paymentId: number;
}
export interface VerifyPaymentRes_Success {
    cardNo: string;
    transactionId: string;
    amount: number;
    invoiceId: string;
    message: string;
    statusCode: number;
}
export interface VerifyPaymentRes_Failed {
    message: string | null;
    errorCode: number;
    statusCode: number;
}
export type VerifyPaymentRes = VerifyPaymentRes_Success | VerifyPaymentRes_Failed;
